return items[0].json.data.children
  .filter(post => {
    const title = post.data.title.toLowerCase();
    return !title.includes("trump") &&
           !title.includes("president") &&
           !title.includes("war") &&
           !title.includes("israel");
  })
  .map(post => {
    const data = post.data;
    return {
      json: {
        post_id: data.id,
        title: data.title,
        selftext:data.selftext,
        score: data.score,
        num_comments: data.num_comments,
        over_18: data.over_18 // to filter NSFW later
      }
    };
  });

return items.filter(item => {
  const { score, num_comments, over_18 } = item.json;
  return score > 60 && num_comments > 40 && over_18 === false;
});

const inputData = $input.all(); // gets all input items

const redditPosts = inputData.filter(i => i.json.post_id);
const existingRows = inputData.filter(i => i.json["Reddit Post ID"]);

const existingPostIds = new Set(
  existingRows
    .map(row => row.json["Reddit Post ID"])
    .filter(id => id !== undefined && id !== null)
    .map(id => id.toString())
);

return redditPosts.filter(item =>
  !existingPostIds.has(item.json.post_id.toString())
);

let count = 1;
return items.map(item => {
  return {
    json: {
      number: count++,
      ...item.json
    }
  };
});

return items.map((item, index) => {
  return {
    json: {
      ...item.json,
      virality_score: item.json.message?.content.trim()
    }
  };
});

return items.filter(item => {
  const score = parseInt(item.json.virality_score, 10);
  return score >= 6;
});
