// Parse the OpenAI response and structure the data
const aiResponse = $input.first().json.choices[0].message.content;

try {
  const classification = JSON.parse(aiResponse);

  // Extract original conversation data
  const conversationData = $('üì® Intercom Webhook').first().json.data.item;

  return {
    json: {
      // Classification results
      category: classification.category,
      sentiment: classification.sentiment,
      urgency: classification.urgency,
      reasoning: classification.reasoning,
      tags: classification.tags || [],

      // Original conversation data
      conversation_id: conversationData.id,
      subject: conversationData.conversation_parts.conversation_parts[0].subject || 'No Subject',
      message: conversationData.conversation_parts.conversation_parts[0].body,
      customer_name: conversationData.user.name,
      customer_email: conversationData.user.email,
      created_at: conversationData.created_at,

      // Formatted data for tasks
      task_title: `[${classification.category.toUpperCase()}] ${conversationData.conversation_parts.conversation_parts[0].subject || 'Support Request'}`,
      task_description: `**Customer:** ${conversationData.user.name} (${conversationData.user.email})\n\n**Original Message:**\n${conversationData.conversation_parts.conversation_parts[0].body}\n\n**AI Classification:**\n- Category: ${classification.category}\n- Sentiment: ${classification.sentiment}\n- Urgency: ${classification.urgency}\n- Reasoning: ${classification.reasoning}\n\n**Conversation ID:** ${conversationData.id}`
    }
  };
} catch (error) {
  throw new Error(`Failed to parse AI response: ${error.message}`);
}

