const apiKey = "{api_key}";
const packageId = "practice.test.daily.testline";

return items.map(item => {
  const keyword = item.json["Keywords"];
  const id = item.json.id;
  const currentRank = item.json["Current Rank"];

  const serpapiUrl = `https://serpapi.com/search.json?engine=google_play&q=${encodeURIComponent(keyword)}&gl=in&hl=en&store=apps&api_key=YOUR_TOKEN_HERE

  return {
    json: {
      ...item.json,          // important: keep all Baserow fields
      id,                   // row ID for update
      currentRank,          // existing rank for history
      packageId,            // fixed app package
      serpapi_url: serpapiUrl
    }
  };
});

const appId = "{app_package_name}";
const results = $json["organic_results"] || [];

let rank = null;
let position = 1;

for (const block of results) {
  if (!block.items) continue;

  for (const app of block.items) {
    if (app.product_id === appId) {
      rank = position;
      break;
    }
    position++;
  }

  if (rank !== null) break;
}

if (rank === null) rank = 0;

return {
  json: {
    id: $json.id,  // will now work ‚úÖ
    keyword: $json.Keywords,
    currentRank: rank,
    previousRank: $json.currentRank || null
  }
};
