// ---------------------------------------------------------------------------
// n8n Code Node - Prepare Workflow JSON for UI-Compatible Export
// ---------------------------------------------------------------------------
//
// GOAL: Produce an output *identical* to the workflow JSON downloaded from
// the n8n UI ‚Üí Same fields, same structure, same order.
// ---------------------------------------------------------------------------

return $input.all().map(item => {
    const w = item.json;

    // Keep exactly the fields that appear in a native n8n export
    // And ensure they are in the correct order
    const cleaned = {
        name: w.name,                        // Workflow name
        nodes: w.nodes || [],               // Workflow nodes
        pinData: w.pinData || {},           // Pinned node data (empty if not set)
        connections: w.connections || {},   // Workflow connections
        active: w.active ?? false,          // Default false like exported UI
        settings: w.settings || {},         // Workflow-level settings
        versionId: w.versionId || "",       // Keep version ID if present
        meta: w.meta || {},                 // Additional meta info
        id: w.id,                           // Workflow unique ID
        tags: w.tags || []                  // Array of tags
    };

    // Return the cleaned JSON, ready for export or GitLab storage
    return {
        json: cleaned
    };
});

// ---------------------------------------------------------------------------
// n8n Code Node - Clean & Normalize Workflow Name
// ---------------------------------------------------------------------------
//
// GOAL: Standardize workflow names by properly formatting [client] tags.
//
// - Detects any existing [client] tags in the name.
// - If a client value exists ‚Üí Normalize to `[client : NAME]`.
// - If the tag exists but is empty ‚Üí Remove it completely.
// - If no tag is present ‚Üí Leave the name unchanged.
//
// Output structure matches the original item, only the `name` is updated.
// ---------------------------------------------------------------------------

return $input.all().map(item => {
    const w = item.json;

    // Regex to match patterns like:
    // [clientXYZ], [client: XYZ], [client : xyz], [client xyz]
    // [ Client: XYZ] with optional space after [
    // Also matches empty tags: [client], [client:], [client : ]
    const clientTag = /\[\s*client\s*:?\s*([^\]\r\n]*)\]/i;

    let name = String(w.name || "").trim();
    const match = name.match(clientTag);

    if (match) {
        // Extract raw client value from the tag
        const rawClient = match[1] ? match[1].trim() : "";

        if (rawClient) {
            // Normalize client name to uppercase
            const clientName = rawClient.toUpperCase();

            // Remove the original tag completely
            name = name.replace(clientTag, "").trim();

            // Rebuild the name with the normalized format
            name = `[client : ${clientName}] ${name}`;
        } else {
            // If tag exists but has no value ‚Üí remove it entirely
            name = name.replace(clientTag, "").trim();
        }
    }

    // Return the updated workflow JSON (only name is modified)
    return {
        json: {
            ...w,
            name
        }
    };
});

