// ---------------------------------------------------------------------------
// n8n Code Node - Prepare GitLab File Path (Dedicated Node)
// ---------------------------------------------------------------------------
//
// GOAL: Generate the GitLab storage path for each workflow without polluting
//       the workflow JSON that will be versioned. File name is fixed on ID
//       to avoid duplication when the workflow name changes.
// ---------------------------------------------------------------------------

return $input.all().map(item => {
    const w = item.json; // Current workflow data

    // --------------------------------------------------------
    // Helper: normalize accents & slugify safely
    // --------------------------------------------------------
    const toSlug = (s) => String(s || '')
        .normalize('NFKD')                // decompose accented characters
        .replace(/[\u0300-\u036f]/g, '')  // remove diacritics
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')      // replace invalid chars with hyphens
        .replace(/^-+|-+$/g, '');         // trim hyphens at start/end

    // --------------------------------------------------------
    // 1. Extract and normalize the client slug if tag exists
    // --------------------------------------------------------
    const clientTag = /\[client\s*:?\s*([^\]\r\n]*)\]/i;
    const match = (w.name || '').match(clientTag);

    let clientSlug = "unassigned"; // Default if no [client: ...] tag is found
    if (match && match[1].trim()) {
        clientSlug = toSlug(match[1].trim()) || "unassigned";
    }

    // --------------------------------------------------------
    // 2. Build the GitLab storage path (ID-based, rename-proof)
    // --------------------------------------------------------
    const basePath = $('Set Global GitLab Variables').first().json.gitlab_workflow_path; // From declared variables
    const filePath = `${basePath}/${clientSlug}/${w.id}.json`;

    // --------------------------------------------------------
    // 3. Return ONLY the GitLab file path
    // --------------------------------------------------------
    return {
        json: {
            gitlab_file_path: filePath
        }
    };
});

// n8n Code Node ‚Äî Summarize Backup Results
// ----------------------------------------

const items = $input.all();

// Init counters
let recap = {
  created: 0,
  updated: 0,
  unchanged: 0,
  total: items.length,
};

for (const item of items) {
  const status = item.json.status;
  if (status && recap.hasOwnProperty(status)) {
    recap[status]++;
  }
}

// Build output
return [
  {
    json: {
      execution_type: items[0]?.json.execution_type || "unknown",
      execution_time: items[0]?.json.execution_time || new Date().toISOString(),
      recap,
    },
  },
];
