/* Filter unpublished + map fields safely */
const out = [];
for (const item of items) {
  const page = item.json;
  const id = page.id;
  const p = page.properties || {};
  const g = (prop) => {
    if (!prop) return '';
    if (prop.type === 'title' && prop.title?.length) return prop.title.map(t=>t.plain_text).join('');
    if (prop.type === 'rich_text' && prop.rich_text?.length) return prop.rich_text.map(t=>t.plain_text).join('');
    if (prop.type === 'url') return prop.url || '';
    if (prop.type === 'checkbox') return !!prop.checkbox;
    if (prop.type === 'multi_select') return prop.multi_select.map(o=>o.name);
    if (prop.type === 'select') return prop.select?.name || '';
    if (prop.type === 'date') return prop.date?.start || '';
    return '';
  };
  const isPublished = p.published ? !!p.published.checkbox : false;
  if (isPublished) continue;

  const title = g(p.title) || page.properties?.Name?.title?.[0]?.plain_text || '';
  const angle = g(p.angle);
  const tags = p.tags ? (p.tags.type === 'multi_select' ? p.tags.multi_select.map(o=>o.name) : g(p.tags)) : '';
  const primary_link = g(p.primary_link);
  const reference_links = g(p.reference_links);
  const images = g(p.images);
  const notes = g(p.notes);
  const target_audience = g(p.target_audience);
  const canonical_url = g(p.canonical_url);
  const language = g(p.language);
  const slug = g(p.slug);

  out.push({ json: {
    notion_page_id: id,
    title,
    angle,
    tags,
    primary_link,
    reference_links,
    images,
    notes,
    target_audience,
    canonical_url,
    language,
    slug
  }});
}
return out;

/**
 * Node 11 ‚Äî Function (Parse & Validate JSON from OpenAI)
 * Soft-handles safety_flags (warnings) instead of throwing.
 */

// ---------- 1) Extract message.content ----------
const input = items[0].json;

function extractContent(nodeOut) {
  // n8n OpenAI nodes vary; support a few shapes:

  // A) Array of { message: { content } }
  if (Array.isArray(nodeOut) && nodeOut[0]?.message?.content !== undefined) {
    return nodeOut[0].message.content;
  }
  // B) Chat Completions
  if (nodeOut?.data?.choices?.[0]?.message?.content !== undefined) {
    return nodeOut.data.choices[0].message.content;
  }
  // C) OpenAI node returning raw string JSON at top level
  if (typeof nodeOut === "string") {
    return nodeOut;
  }
  // D) Flattened shape
  if (nodeOut?.message?.content !== undefined) {
    return nodeOut.message.content;
  }
  // E) Sometimes models already return parsed object
  if (nodeOut?.content !== undefined) {
    return nodeOut.content;
  }
  throw new Error("Node11: could not locate message.content from OpenAI output");
}

let content = extractContent(input);

// ---------- 2) Parse to object ----------
let parsed;
if (content && typeof content === "object") {
  parsed = content;
} else if (typeof content === "string") {
  let txt = content.trim();
  if (txt.startsWith("```")) {
    txt = txt.replace(/^```(?:json)?\s*/i, "").replace(/\s*```$/i, "");
  }
  txt = txt
    .replace(/[\u2018\u2019]/g, "'")
    .replace(/[\u201C\u201D]/g, '"')
    .replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F]/g, " ")
    .replace(/,\s*([}\]])/g, "$1");

  // escape raw newlines inside strings
  (function escapeNewlinesInStrings() {
    let out = "", inStr = false, esc = false;
    for (let i = 0; i < txt.length; i++) {
      const ch = txt[i];
      if (!inStr) { if (ch === '"') inStr = true; out += ch; continue; }
      if (esc) { out += ch; esc = false; continue; }
      if (ch === "\\") { out += ch; esc = true; continue; }
      if (ch === '"') { inStr = false; out += ch; continue; }
      if (ch === "\n" || ch === "\r") { out += "\\n"; continue; }
      out += ch;
    }
    txt = out;
  })();

  try {
    parsed = JSON.parse(txt);
  } catch (e) {
    throw new Error("Node11: JSON.parse failed after repair: " + e.message);
  }
} else {
  throw new Error("Node11: message.content is neither object nor string");
}

// ---------- 3) Validate + normalize ----------
const errs = [];
const warn = [];
const isNonEmpty = (s) => typeof s === "string" && s.trim().length > 0;
const arrOfStr = (a) => Array.isArray(a) ? a.map(x => String(x)).filter(Boolean) : [];

if (!parsed.blog) errs.push("blog missing");
else {
  if (!isNonEmpty(parsed.blog.title)) errs.push("blog.title missing");
  if (!isNonEmpty(parsed.blog.slug)) errs.push("blog.slug missing");
  if (!isNonEmpty(parsed.blog.markdown)) errs.push("blog.markdown missing");
  if (parsed.blog.slug && parsed.blog.slug.length > 100) errs.push("blog.slug > 100 chars");
  parsed.blog.keywords = arrOfStr(parsed.blog.keywords);
  parsed.blog.image_alt_texts = arrOfStr(parsed.blog.image_alt_texts);
  if (
    parsed.blog.canonical_url !== null &&
    parsed.blog.canonical_url !== undefined &&
    typeof parsed.blog.canonical_url !== "string"
  ) errs.push("blog.canonical_url must be string or null");
}

if (!parsed.linkedin) errs.push("linkedin missing");
else {
  if (!isNonEmpty(parsed.linkedin.final_text)) errs.push("linkedin.final_text missing");
  // hashtags soft window
  parsed.linkedin.hashtags = arrOfStr(parsed.linkedin.hashtags);
  if (parsed.linkedin.hashtags.length > 8) {
    parsed.linkedin.hashtags = parsed.linkedin.hashtags.slice(0, 8);
  }
  // char guard
  let ft = String(parsed.linkedin.final_text || "");
  if (ft.length > 1200) {
    let cut = ft.slice(0, 1180);
    const lastBreak = Math.max(cut.lastIndexOf("\n"), cut.lastIndexOf("."));
    if (lastBreak > 800) cut = cut.slice(0, lastBreak + 1);
    parsed.linkedin.final_text = cut;
    warn.push("linkedin.final_text truncated to 1200 chars");
  }
}

// Safety flags: treat as warnings (expose downstream)
if (!Array.isArray(parsed.safety_flags)) parsed.safety_flags = [];
if (parsed.safety_flags.length) {
  warn.push("safety_flags present: " + parsed.safety_flags.join(", "));
}

// Hard errors?
if (errs.length) {
  throw new Error("Node11 validation failed: " + errs.join("; "));
}

// ---------- 4) Merge with Normalize Arrays (Node 9), return ----------
let norm = {};
try {
  const n = $items("Function (Normalize Arrays)", 0, $runIndex);
  if (Array.isArray(n) && n[0] && n[0].json) norm = n[0].json;
} catch (_) {}

return [{
  json: {
    ...norm,
    openai: parsed,
    warnings: warn
  }
}];

// Normalize arrays + carry configs
function toArray(v){
  if (Array.isArray(v)) return v;
  if (typeof v === 'string'){
    const s = v.trim();
    if (!s) return [];
    try { const parsed = JSON.parse(s); if (Array.isArray(parsed)) return parsed; } catch(e) {}
    return s.split(',').map(x=>x.trim()).filter(Boolean);
  }
  return [];
}

// Pull config from the Set (Configs) node
// Simple form:
const cfg = $node["Set (Configs)"].json;
// Robust form (if you prefer):
// const cfg = $items("Set (Configs)", 0, $runIndex)[0].json;

const i = items[0].json;

return [{
  json: {
    NOTION_DB_ID: cfg.NOTION_DB_ID,
    NOTION_PAGE_PARENT: cfg.NOTION_PAGE_PARENT,
    LINKEDIN_PERSON_URN: cfg.LINKEDIN_PERSON_URN,
    OPENAI_MODEL: cfg.OPENAI_MODEL,
    EMAIL_TO: cfg.EMAIL_TO,

    notion_page_id: i.notion_page_id,
    title: i.title || '',
    angle: i.angle || '',
    tags: toArray(i.tags),
    primary_link: i.primary_link || '',
    reference_links: toArray(i.reference_links),
    images: toArray(i.images),
    notes: i.notes || '',
    target_audience: i.target_audience || '',
    canonical_url: i.canonical_url || '',
    language: i.language || '',
    slug: i.slug || ''
  }
}];

