function fnv1a(str) {
  let h = 0x811c9dc5;
  for (let i = 0; i < str.length; i++) {
    h ^= str.charCodeAt(i);
    h += (h << 1) + (h << 4) + (h << 7) + (h << 8) + (h << 24);
  }
  return (h >>> 0).toString(16);
}

return items.map(item => {
  const url = item.json.link || item.json.url || '';
  const title = item.json.title || '';
  const content = (item.json.content || item.json['content:encoded'] || item.json.contentSnippet || '').toString();

  const text = [title, content]
    .join('\n')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  item.json.url = url;
  item.json.title = title;
  item.json.text_rss = text.slice(0, 6000);
  item.json.url_hash = fnv1a(String(url).toLowerCase());
  return item;
});

const keepDays = 14;
const maxEntries = 800;
const now = Date.now();

const store = $getWorkflowStaticData('global');
if (!store.seen) store.seen = {};

return items.map(item => {
  const key = item.json.url_hash;
  const isNew = !store.seen[key];
  store.seen[key] = now;

  for (const [k, t] of Object.entries(store.seen)) {
    if (now - t > keepDays * 86400000) delete store.seen[k];
  }
  const entries = Object.entries(store.seen);
  if (entries.length > maxEntries) {
    entries.sort((a,b) => a[1] - b[1]);
    for (let i = 0; i < entries.length - maxEntries; i++) delete store.seen[entries[i][0]];
  }

  item.json._isNew = isNew;
  return item;
});

