return items.map(item => {
  const raw = item.json.text || item.json.data || item.json.output || '';
  let o = {};
  try { o = typeof raw === 'string' ? JSON.parse(raw) : raw; } catch {}
  item.json.summary   = o.summary || '';
  item.json.sentiment = (o.sentiment || 'neutral').toLowerCase();
  item.json.keywords  = Array.isArray(o.keywords) ? o.keywords : [];
  item.json.flags     = Array.isArray(o.flags) ? o.flags : [];
  return item;
});

return items.map(item => {
  const html = item.json.data || item.json.body || '';
  const plain = html
    .replace(/<script[\s\S]*?<\/script>/gi, ' ')
    .replace(/<style[\s\S]*?<\/style>/gi, ' ')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  const txt = (plain && plain.length > 500 ? plain : item.json.text_rss).slice(0, 9000);
  item.json.text_for_llm = txt;
  item.json.data = txt;
  return item;
});
