// Take the first input (your wrapper object) and explode its `output` array
const outputs = $input.first().json.output;

const out = [];

for (const o of outputs) {
  const { character_prompt, action_prompt } = o ?? {};
  out.push({
    json: {
      scene: {
        character_prompt,
        action_prompt,
      },
    },
  });
}

return out;
