return $input.all().map(item => {
    const timestamp = item.json.timestamp;

    // Create a Date object using the timestamp
    const triggerDate = new Date(timestamp);

    // Array of month names
    const monthNames = ["January", "February", "March", "April", "May", "June",
                        "July", "August", "September", "October", "November", "December"];

    // Calculate yesterday's date
    const yesterday = new Date(triggerDate);
    yesterday.setDate(triggerDate.getDate() - 1);

    // Format today's date
    const todayDay = String(triggerDate.getDate()).padStart(2, '0');
    const todayMonth = triggerDate.getMonth();
    const todayYear = triggerDate.getFullYear();
    const formattedToday = `${todayDay}-${monthNames[todayMonth]}-${todayYear}`;
    const isoToday = `${todayYear}-${String(todayMonth + 1).padStart(2, '0')}-${todayDay}`;

    // Format yesterday's date
    const day = String(yesterday.getDate()).padStart(2, '0');
    const month = yesterday.getMonth();
    const year = yesterday.getFullYear();
    const formattedYesterday = `${day}-${monthNames[month]}-${year}`;
    const isoYesterday = `${year}-${String(month + 1).padStart(2, '0')}-${day}`;

    // Return the result as an object
    return {
        json: {
            formattedToday: formattedToday,
            isoToday: isoToday,
            formattedYesterday: formattedYesterday,
            isoYesterday: isoYesterday,
        }
    };
});

