const base = $input.first().json || {};
const style_guide = [
  "Use first person a few times (‚ÄúI‚Äù), friendly and practical.",
  "Medium‚Äìlow formality. No fluff or buzzwords.",
  "Start with a concrete hook tied to one headline.",
  "Explain what happened, why it matters, who it affects, and what to do next.",
  "Embed 4‚Äì8 inline links to reputable external sources using descriptive anchor text.",
  "Name the source in the sentence when you cite a fact (e.g., Reuters, WHO, CISA).",
  "Clean HTML only: <p>, <h2>, <ul>, <ol>, <li>, <strong>, <em>, <a>.",
  "Short paragraphs (2‚Äì4 sentences).",
  "Include sections: What happened; Why it matters; Winners/losers or risks; Actionable plan; FAQ."
].join("\n");

const do_list = [
  "Pick one headline as the lead and summarize it in 2‚Äì3 sentences with a link.",
  "Pull 1‚Äì2 supporting links from trusted domains.",
  "Add a 4‚Äìstep action plan tailored to operators/founders.",
  "End with a grounded takeaway."
];

const dont_list = [
  "Don‚Äôt speculate without saying it‚Äôs an opinion.",
  "Don‚Äôt add links to random low-quality blogs.",
  "Don‚Äôt use clickbait."
];

return [{
  json: {
    ...base,
    style_guide,
    dos: do_list,
    donts: dont_list
  }
}];

// Ultra-robust topic extraction for chat/Telegram/webhook

function firstString(arr) {
  for (const v of arr) {
    if (typeof v === 'string' && v.trim()) return v.trim();
  }
  return '';
}

// If using n8n Chat Trigger, often it's in messages[]
const msgs = Array.isArray($json.messages)
  ? $json.messages.map(m => m?.content).filter(Boolean).join('\n')
  : '';

const text = firstString([
  $json.text,
  $json.message?.text,
  $json.message?.content,
  $json.prompt,
  $json.query,
  $json.input,
  $json.body?.text,
  $json.body?.message,
  $json.payload?.text,
  $json.content,
  msgs,
  (typeof $json === 'string' ? $json : '')
]);

let topic = String(text || '').trim();

// Allow `/topic ‚Ä¶` or `topic: ‚Ä¶` style commands
topic = topic.replace(/^\/topic\s+/i, '').replace(/^topic:\s*/i, '').trim();

if (!topic) {
  return [{
    json: {
      topic: 'Scientists Develop Plant-Based Plastic That Is Self-Repairing and Antimicrobial - Technology Networks',
      help: 'No topic detected. Send a message with your topic, or use "/topic <subject>".'
    }
  }];
}

return [{ json: { topic } }];

// Build Chat Research Body with resilient topic sourcing
function pickTopic() {
  // Primary: from Extract Topic
  if ($node["Extract Topic"]?.json?.topic) return String($node["Extract Topic"].json.topic);

  // Fallbacks: read raw trigger payloads
  const trig = $node["When chat message received"]?.json || {};
  const msgs = Array.isArray(trig.messages) ? trig.messages.map(m => m?.content).filter(Boolean).join('\n') : '';

  const candidates = [
    $json.topic,
    trig.text,
    trig.message?.text,
    trig.message?.content,
    trig.prompt,
    trig.query,
    trig.input,
    trig.body?.text,
    trig.body?.message,
    trig.payload?.text,
    trig.content,
    msgs
  ].filter(v => typeof v === 'string' && v.trim());

  if (candidates.length) {
    const t = candidates[0].replace(/^\/topic\s+/i, '').replace(/^topic:\s*/i, '').trim();
    if (t) return t;
  }
  return '';
}

const topic = pickTopic();
const style   = $node["Style"]?.json?.style_guide || "Human, practical, medium‚Äìlow formality.";
const dos     = ($node["Style"]?.json?.dos || []).map(s => "‚Ä¢ " + s).join("\n");
const donts   = ($node["Style"]?.json?.donts || []).map(s => "‚Ä¢ " + s).join("\n");

if (!topic) {
  throw new Error("No topic provided. Send a message to the bot with the topic you want researched (e.g., '/topic WA renewable energy in mining').");
}

const body = {
  model: "gpt-4o-mini",
  response_format: { type: "json_object" },
  temperature: 0.6,
  messages: [
    {
      role: "system",
      content:
        "You are a research writer creating long-form, well-cited blog posts for a general audience. " +
        "Always include descriptive, trustworthy external links (with full URLs) to support claims. " +
        "Write between 1700‚Äì1800 words. Use only clean HTML tags: <p>, <h2>, <ul>, <ol>, <li>, <strong>, <em>, <a>. " +
        "Sections to include: What happened/Context; Why it matters; Opportunities/risks; Action plan; FAQ."
    },
    {
      role: "user",
      content:
`TOPIC:
${topic}

STYLE GUIDE:
${style}

DO:
${dos}

DON'T:
${donts}

Return ONLY one JSON object exactly in this shape:
{
  "title": "<compelling title for the post>",
  "content": "<HTML content using only allowed tags>"
}`
    }
  ]
};

return [{ json: body }];

const style = $node["Style"]?.json?.style_guide || "Human, practical, medium‚Äìlow formality.";
const dos = ($node["Style"]?.json?.dos || []).join("\n");
const donts = ($node["Style"]?.json?.donts || []).join("\n");

const article = $json || $node["Build Chat Research Body"]?.json_output || {};

return [{
  json: {
    model: "gpt-4o-mini",
    temperature: 0.4,
    response_format: { type: "json_object" },
    messages: [
      {
        role: "system",
        content:
          "You are an editor. Improve clarity/flow only. Do NOT change topic, claims, links, or overall structure. " +
          "Preserve HTML tags (<p>, <h2>, <ul>, <ol>, <li>, <strong>, <em>, <a>). " +
          "Return a JSON object with keys: title, content."
      },
      {
        role: "user",
        content:
          "STYLE GUIDE:\n" + style +
          "\n\nDO:\n" + dos +
          "\n\nDON'T:\n" + donts +
          "\n\nARTICLE JSON TO POLISH (keep same keys & structure):\n" + JSON.stringify(article)
      }
    ]
  }
}];

// Word Count Guard (after Tone & Format)
function strip(html) { return (html || '').replace(/<[^>]+>/g, ' '); }
const text = strip($json.content || '');
const words = text.trim().split(/\s+/).filter(Boolean).length;

const MIN = 1600;
if (words >= MIN) {
  return [{ json: { ...$json, word_count: words } }];
}

// too short ‚Üí ask GPT to expand
return [{
  json: {
    draft: $json,
    word_count: words,
    need_expand: true
  }
}];

// ---- helpers ----
function slugify(s) {
  return String(s || '')
    .toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-+|-+$/g, '')
    .slice(0, 60);
}

function extractSources(html) {
  if (!html) return '';
  if (/<h2[^>]*>Sources<\/h2>/i.test(html)) return html;

  const urls = Array.from(new Set(
    (html.match(/https?:\/\/[^\s"')<>]+/g) || []).map(u => u.replace(/[.,)\]]+$/,''))
  ));

  if (!urls.length) return html;

  const items = urls.map(u => {
    try {
      const host = new URL(u).hostname.replace(/^www\./,'');
      return `<li><a href="${u}">${host}</a></li>`;
    } catch {
      return `<li><a href="${u}">${u}</a></li>`;
    }
  }).join('');

  return html + `\n\n<h2>Sources</h2><ul>${items}</ul>`;
}

// Detect object vs string safely and only JSON.parse when it's a real string
function coerceObject(j) {
  // 1) Common places where the node already gives an OBJECT
  if (j?.message?.parsed && typeof j.message.parsed === 'object') return j.message.parsed;
  if (j?.choices?.[0]?.message?.parsed && typeof j.choices[0].message.parsed === 'object') {
    return j.choices[0].message.parsed;
  }
  if (j?.message?.content && typeof j.message.content === 'object') return j.message.content;

  // 2) Candidate content (could be string or object)
  let cand =
    j?.choices?.[0]?.message?.content ??
    j?.message?.content ??
    j?.content ??
    '';

  // If it's already an OBJECT, just use it
  if (cand && typeof cand === 'object' && !Array.isArray(cand)) return cand;

  // 3) If it's a STRING, clean & parse
  if (typeof cand === 'string') {
    let s = cand.trim();

    // strip ```json fences if present
    s = s.replace(/^```(?:json)?\s*/i, '').replace(/```$/i, '');

    // isolate outermost JSON object (in case there's prose around it)
    const start = s.indexOf('{');
    const end   = s.lastIndexOf('}');
    if (start !== -1 && end !== -1) s = s.slice(start, end + 1);

    try {
      return JSON.parse(s);
    } catch (e) {
      throw new Error(
        'Could not parse model JSON. ' +
        'Type=' + typeof cand + ' Preview:\n' + s.slice(0, 400)
      );
    }
  }

  // 4) Nothing usable
  throw new Error('No usable payload: got ' + typeof cand);
}

// ---- MAIN ----
const obj = coerceObject($json);         // <- ONLY parses when needed

const title = String(obj.title || 'Untitled').trim();
let content = String(obj.content || '');
content = extractSources(content);

const image_filename = `${slugify(title)}.jpg`;

return [{ json: { title, content, image_filename } }];

// Node: Keep Generation Id
const id =
  $json.sdGenerationJob?.generationId ||
  $json.generationId ||
  $json.generations_by_pk?.id ||
  $json.id;

if (!id) throw new Error('No generationId found in previous node output');

return [{ json: { generationId: id } }];
