// Initialize the default output structure to be returned in case of error or empty input.
const defaultOutput = {
    "conversation_text": ""
};

try {
    // --- INPUTS ---
    // Assuming the primary message payload is from the first input item.
    const messages = $('Load Chatwoot Conversation History').first().json.payload;

    // Ensure 'messages' is a valid array and messages.length > 0 before proceeding.
    if (Array.isArray(messages) && messages.length > 0) {

        // Map over each message to transform it into the desired format.
        const fullHistory = messages.map(msg => {
            // RULE 1: Determine the role based on the sender type.
            const role = (msg.sender && msg.sender.type === 'contact') ? 'user' : 'assistant';

            // RULE 2: Replace '/start' content with 'Hello!'.
            // FIX: Default null content to an empty string to ensure safe concatenation.
            let content = msg.content || '';

            if (content === '/start') {
                content = 'Hello!';
            }

            return {
                role,
                content
            };
        });

        // --- TASK 2: Remove Messages with Empty Content ---
        // Filter the generated history to exclude any messages where the content is empty or just whitespace.
        const filteredHistory = fullHistory.filter(msg => msg.content && msg.content.trim() !== '');

        // --- TASK 3: Create a single text string from the entire history ---
        // This now uses the entire filteredHistory instead of a slice.
        const conversationText = filteredHistory
            .map(msg => `[${msg.role.toUpperCase()}] : ${msg.content}`)
            .join(", \n");

        // Return the final string in the expected n8n JSON format.
        return [{
            json: {
                "conversation_text": conversationText
            }
        }];

    }

    // If the input was invalid or empty, return the default empty structure.
    return [{
        json: defaultOutput
    }];

} catch (error) {
    // RULE 3: If any error occurs during execution, return the default empty structure.
    // This ensures the workflow doesn't fail.
    console.error("Error processing history:", error.message);
    return [{
        json: defaultOutput
    }];
}
