// Topic-Based Document Chunker - Smart Content Division
const content = $input.first().json.body.content;
const maxChunkSize = 4000; // Larger chunks for complete topics
const minChunkSize = 1000; // Minimum viable chunk size

console.log('Original content length:', content.length, 'characters');

// If content is small enough, don't chunk
if (content.length <= maxChunkSize) {
  console.log('Content is small enough, no chunking needed');
  return items;
}

console.log('Content is large, applying topic-based chunking');

function topicBasedChunk(text, maxSize, minSize) {
  const chunks = [];

  // Strategy 1: Split by clear section headers (most common in business docs)
  let sections = [];

  // Look for various header patterns
  const headerPatterns = [
    /\n\s*(?:\d+\.|Chapter \d+|Section \d+|Part \d+)[\s:]/gi,     // 1. Section 1: or Chapter 1:
    /\n\s*[A-Z][A-Z\s]{10,}(?:\n|$)/g,                           // UPPERCASE HEADERS
    /\n\s*#{1,3}\s+[^\n]+/g,                                     // # Markdown headers
    /\n\s*[^\n]*(?:Policy|Procedure|Guidelines|Rules)[\s:]/gi,   // Policy headers
    /\n\s*[^\n]*(?:Overview|Introduction|Summary|Conclusion)[\s:]?/gi // Common section names
  ];

  // Try each pattern to find the best split
  for (const pattern of headerPatterns) {
    const matches = text.match(pattern);
    if (matches && matches.length >= 2) {
      console.log(`Found ${matches.length} sections using pattern:`, pattern.toString().substring(0, 50));
      sections = text.split(pattern);
      break;
    }
  }

  // Fallback: Split by paragraph breaks if no clear headers
  if (sections.length < 2) {
    console.log('No clear headers found, splitting by paragraphs');
    sections = text.split(/\n\s*\n\s*\n/); // Double line breaks
  }

  // Strategy 2: Group sections into logical chunks
  let currentChunk = '';
  let chunkStartIndex = 0;

  for (let i = 0; i < sections.length; i++) {
    const section = sections[i].trim();
    if (!section) continue;

    const sectionWithNewline = (currentChunk ? '\n\n' : '') + section;
    const potentialChunk = currentChunk + sectionWithNewline;

    // Decision logic for chunk boundaries
    if (potentialChunk.length > maxSize && currentChunk.length >= minSize) {
      // Current chunk is full and viable - save it
      chunks.push({
        chunk_id: chunks.length + 1,
        content: currentChunk.trim(),
        topic_hint: extractTopicHint(currentChunk),
        sections_count: i - chunkStartIndex,
        length: currentChunk.length
      });

      console.log(`Created chunk ${chunks.length}: "${chunks[chunks.length-1].topic_hint}" (${currentChunk.length} chars)`);

      // Start new chunk
      currentChunk = section;
      chunkStartIndex = i;

    } else {
      // Add section to current chunk
      currentChunk = potentialChunk;
    }
  }

  // Don't forget the last chunk
  if (currentChunk.trim()) {
    chunks.push({
      chunk_id: chunks.length + 1,
      content: currentChunk.trim(),
      topic_hint: extractTopicHint(currentChunk),
      sections_count: sections.length - chunkStartIndex,
      length: currentChunk.length
    });

    console.log(`Created final chunk ${chunks.length}: "${chunks[chunks.length-1].topic_hint}" (${currentChunk.length} chars)`);
  }

  return chunks;
}

// Helper function to extract topic hints from content
function extractTopicHint(text) {
  const firstLine = text.split('\n')[0].trim();

  // Look for clear topic indicators
  const topicPatterns = [
    /(?:policy|procedure|guideline|rule|protocol)s?\s+(?:for|on|about)\s+([^.]+)/gi,
    /(?:section|chapter|part)\s+\d+[:\s]+([^.\n]+)/gi,
    /^([^.\n]{10,50})(?:\.|:|\n)/,  // First substantial line
    /([A-Z][^.\n]{10,40})/         // Capitalized phrases
  ];

  for (const pattern of topicPatterns) {
    const match = firstLine.match(pattern);
    if (match && match[1]) {
      return match[1].trim().substring(0, 40);
    }
  }

  // Fallback: first meaningful words
  const words = text.split(/\s+/).slice(0, 6).join(' ');
  return words.length > 50 ? words.substring(0, 47) + '...' : words;
}

// Apply the chunking
const chunks = topicBasedChunk(content, maxChunkSize, minChunkSize);

console.log('=== CHUNKING RESULTS ===');
console.log(`Created ${chunks.length} topic-based chunks:`);
chunks.forEach((chunk, i) => {
  console.log(`  ${i+1}. "${chunk.topic_hint}" - ${chunk.length} chars, ${chunk.sections_count} sections`);
});

// Return chunks with enhanced metadata
const result = chunks.map(chunk => ({
  json: {
    ...items[0].json, // Keep original metadata
    content: chunk.content,
    chunk_info: {
      chunk_id: chunk.chunk_id,
      total_chunks: chunks.length,
      chunk_size: chunk.length,
      topic_hint: chunk.topic_hint,
      sections_in_chunk: chunk.sections_count,
      chunking_strategy: 'topic-based'
    },
    is_chunked: chunks.length > 1,
    original_content_length: content.length
  }
}));

console.log('Returning', result.length, 'topic-based chunks for AI processing');
return result;

// Fixed merge code for OpenAI message format
console.log('=== MERGE PROCESSING ===');
console.log('Total items received:', items.length);

let allModules = [];
let processingErrors = [];

// Process each item from the AI Content Analyzer
for (let i = 0; i < items.length; i++) {
  const item = items[i];
  console.log('Processing item', i + 1);

  if (item && item.json && item.json.message && item.json.message.content) {
    const aiResponseString = item.json.message.content;
    console.log('Found AI response string, length:', aiResponseString.length);

    try {
      // Parse the JSON string from the AI response
      const parsedModules = JSON.parse(aiResponseString);

      if (Array.isArray(parsedModules)) {
        console.log('Successfully parsed', parsedModules.length, 'modules from item', i + 1);
        allModules = allModules.concat(parsedModules);
      } else {
        console.log('Parsed content is not an array for item', i + 1);
        processingErrors.push('Item ' + (i + 1) + ' - Not an array');
      }

    } catch (error) {
      console.log('JSON parse error for item', i + 1, ':', error.message);
      processingErrors.push('Item ' + (i + 1) + ' - Parse error: ' + error.message);
    }

  } else {
    console.log('Item', i + 1, 'missing expected structure');
    processingErrors.push('Item ' + (i + 1) + ' - Missing message.content');
  }
}

console.log('=== PROCESSING SUMMARY ===');
console.log('Total modules extracted:', allModules.length);
console.log('Processing errors:', processingErrors.length);

// Remove duplicates based on title
const uniqueModules = [];
const seenTitles = new Set();

for (let i = 0; i < allModules.length; i++) {
  const module = allModules[i];
  const normalizedTitle = (module.title || 'untitled').toLowerCase().trim();

  if (!seenTitles.has(normalizedTitle)) {
    seenTitles.add(normalizedTitle);
    uniqueModules.push(module);
  } else {
    console.log('Skipping duplicate module:', module.title);
  }
}

console.log('Unique modules after deduplication:', uniqueModules.length);

// Create final result with all modules
const result = {
  course_id: 'course_' + Date.now(),
  total_modules: uniqueModules.length,
  estimated_total_time: uniqueModules.length * 3 + ' minutes',
  difficulty_level: uniqueModules.length <= 3 ? 'beginner' : uniqueModules.length <= 5 ? 'intermediate' : 'advanced',
  created_at: new Date().toISOString(),

  processing_info: {
    chunks_processed: items.length,
    modules_found: allModules.length,
    modules_after_dedup: uniqueModules.length,
    processing_errors: processingErrors
  },

  modules: uniqueModules.map((module, index) => ({
    id: 'module_' + (index + 1),
    sequence: index + 1,
    title: module.title || 'Module ' + (index + 1),
    key_concept: module.key_concept || 'Key concept not provided',
    content: module.explanation || 'Content not provided',
    example: module.practical_example || 'Example not provided',
    quiz: module.quick_quiz_question || 'Question not provided',
    estimated_minutes: 3,

    // Multi-channel formats
    formats: {
      email: {
        subject: 'üìö Module ' + (index + 1) + ': ' + (module.title || 'Untitled'),
        body: 'üéØ **Key Concept**: ' + (module.key_concept || 'N/A') + '\n\nüìñ **Learn This**:\n' + (module.explanation || 'N/A') + '\n\nüí° **Real Example**: ' + (module.practical_example || 'N/A') + '\n\n‚ùì **Quick Check**: ' + (module.quick_quiz_question || 'N/A') + '\n\n‚è±Ô∏è **Time**: 3 minutes'
      },

      slack: {
        text: 'üìö *Module ' + (index + 1) + ': ' + (module.title || 'Untitled') + '*\n\nüéØ *Key*: ' + (module.key_concept || 'N/A') + '\n\n' + (module.explanation || 'N/A') + '\n\nüí° *Example*: ' + (module.practical_example || 'N/A') + '\n\n‚ùì *Quiz*: ' + (module.quick_quiz_question || 'N/A') + '\n\n‚è±Ô∏è 3 min read'
      },

      mobile: 'üìö ' + (module.title || 'Untitled') + '\n\nüéØ ' + (module.key_concept || 'N/A') + '\n\n' + (module.explanation || 'N/A') + '\n\nüí° ' + (module.practical_example || 'N/A') + '\n\n‚ùì ' + (module.quick_quiz_question || 'N/A') + '\n\n‚è±Ô∏è 3 min'
    }
  }))
};

console.log('=== FINAL RESULT ===');
console.log('Returning course with', result.total_modules, 'modules');
console.log('Module titles:', result.modules.map(m => m.title));

return [{ json: result }];

// Google Docs Formatter - Handles Individual Modules
console.log('Processing', items.length, 'module items');

// Check if we have individual modules or course data
let modules = [];
let courseMetadata = {};

if (items.length === 1 && items[0].json.modules) {
  // Single course object with modules array
  const courseData = items[0].json;
  modules = courseData.modules;
  courseMetadata = courseData;
} else {
  // Multiple individual module objects
  modules = items.map(item => item.json);

  // Extract course metadata from first module or create defaults
  courseMetadata = {
    course_id: 'Generated Course',
    total_modules: modules.length,
    estimated_total_time: `${modules.length * 3} minutes`,
    difficulty_level: modules.length <= 3 ? 'beginner' : modules.length <= 5 ? 'intermediate' : 'advanced',
    created_at: new Date().toISOString()
  };
}

// Sort modules by sequence
modules.sort((a, b) => (a.sequence || 0) - (b.sequence || 0));

console.log('Found', modules.length, 'modules to process');

// Extract course metadata with fallbacks
const courseTitle = "Template Submission Guidelines - Micro-Learning Course";
const courseId = courseMetadata.course_id || 'Generated Course';
const totalModules = courseMetadata.total_modules || modules.length;
const estimatedTime = courseMetadata.estimated_total_time || `${modules.length * 3} minutes`;
const difficultyLevel = courseMetadata.difficulty_level || 'intermediate';
const createdDate = courseMetadata.created_at ? new Date(courseMetadata.created_at).toLocaleDateString() : new Date().toLocaleDateString();

// Start building the Google Docs formatted content
let googleDocsContent = `${courseTitle}

COURSE OVERVIEW
Course ID: ${courseId}
Total Modules: ${totalModules}
Estimated Completion Time: ${estimatedTime}
Difficulty Level: ${difficultyLevel.toUpperCase()}
Created: ${createdDate}

YOUR_AWS_SECRET_KEY_HERE=======

`;

// Process each module
modules.forEach((module, index) => {
  const moduleNum = module.sequence || (index + 1);
  const title = module.title || `Module ${moduleNum}`;
  const duration = module.estimated_minutes || 3;
  const keyConcept = module.key_concept || 'Key concept not provided';
  const content = module.content || 'Content not provided';
  const example = module.example || module.practical_example || 'Example not provided';
  const quiz = module.quiz || module.quick_quiz_question || 'Quiz question not provided';

  googleDocsContent += `MODULE ${moduleNum}: ${title}
Duration: ${duration} minutes

KEY CONCEPT
${keyConcept}

CONTENT
${content}

PRACTICAL EXAMPLE
${example}

KNOWLEDGE CHECK
${quiz}

---

`;
});

// Add completion section
googleDocsContent += `COURSE COMPLETION
Congratulations! You have completed all ${totalModules} modules of the Template Submission Guidelines course.

Total time invested: ${estimatedTime}
Knowledge areas covered:
`;

// Add module summary
modules.forEach((module, index) => {
  const title = module.title || `Module ${index + 1}`;
  googleDocsContent += `‚Ä¢ ${title}\n`;
});

googleDocsContent += `
For additional questions or clarification on template submission guidelines, please refer to the official documentation or contact support.

Document generated: ${new Date().toLocaleString()}`;

// Return the formatted content
const result = {
  google_docs_content: googleDocsContent,
  metadata: {
    original_course_id: courseId,
    modules_processed: modules.length,
    formatted_at: new Date().toISOString(),
    content_length: googleDocsContent.length,
    estimated_pages: Math.ceil(googleDocsContent.length / 2500)
  },
  debug_info: {
    input_items: items.length,
    modules_found: modules.length,
    first_module_title: modules[0]?.title || 'N/A',
    data_format: items.length === 1 ? 'single_course' : 'individual_modules'
  }
};

console.log('Google Docs format generated successfully');
console.log('Content length:', googleDocsContent.length, 'characters');
console.log('Modules processed:', modules.length);

return [{ json: result }];

