// Same processing logic as scheduled audit
const response = $input.first().json;
const webhookData = $('Format Webhook Input').item.json;

// Extract key metrics
const lighthouseResult = response.lighthouseResult;
const categories = lighthouseResult.categories;
const audits = lighthouseResult.audits;

// Core Web Vitals
const lcp = audits['largest-contentful-paint']?.displayValue || 'N/A';
const fid = audits['max-potential-fid']?.displayValue || 'N/A';
const cls = audits['cumulative-layout-shift']?.displayValue || 'N/A';

// Performance score (0-100)
const performanceScore = Math.round(categories.performance.score * 100);

// Key recommendations
const recommendations = [];
if (audits['unused-css-rules'] && audits['unused-css-rules'].score < 0.5) {
  recommendations.push('Remove unused CSS');
}
if (audits['render-blocking-resources'] && audits['render-blocking-resources'].score < 0.5) {
  recommendations.push('Eliminate render-blocking resources');
}
if (audits['unminified-css'] && audits['unminified-css'].score < 0.5) {
  recommendations.push('Minify CSS');
}
if (audits['unminified-javascript'] && audits['unminified-javascript'].score < 0.5) {
  recommendations.push('Minify JavaScript');
}
if (audits['uses-optimized-images'] && audits['uses-optimized-images'].score < 0.5) {
  recommendations.push('Optimize images');
}

return {
  json: {
    success: true,
    audit_date: new Date().toISOString(),
    url: webhookData.URL,
    site_name: webhookData.Site_Name,
    device: webhookData.device,
    performance_score: performanceScore,
    core_web_vitals: {
      lcp: lcp,
      fid: fid,
      cls: cls
    },
    recommendations: recommendations,
    full_report_url: response.id ? `https://pagespeed.web.dev/report?url=${encodeURIComponent(webhookData.URL)}` : 'N/A',
    raw_scores: {
      performance: categories.performance.score,
      accessibility: categories.accessibility?.score || null,
      'best-practices': categories['best-practices']?.score || null,
      seo: categories.seo?.score || null
    }
  }
};

const response = $input.first().json;
const siteData = $('Load Sites from Google Sheet').item.json;

// Extract key metrics
const lighthouseResult = response.lighthouseResult;
const categories = lighthouseResult.categories;
const audits = lighthouseResult.audits;

// Core Web Vitals
const lcp = audits['largest-contentful-paint']?.displayValue || 'N/A';
const fid = audits['max-potential-fid']?.displayValue || 'N/A';
const cls = audits['cumulative-layout-shift']?.displayValue || 'N/A';

// Performance score (0-100)
const performanceScore = Math.round(categories.performance.score * 100);

// IMPROVED ALERT LOGIC
const alertThreshold = parseInt(siteData.Alert_Threshold) || 75; // Ensure it's a number
const shouldAlert = performanceScore < alertThreshold;

// SEVERITY LEVELS for better alerting
let alertSeverity = 'info';
let alertMessage = 'Performance within acceptable range';

if (performanceScore < 30) {
    alertSeverity = 'critical';
    alertMessage = 'üö® CRITICAL: Extremely poor performance';
} else if (performanceScore < 50) {
    alertSeverity = 'high';
    alertMessage = '‚ö†Ô∏è HIGH: Poor performance affecting user experience';
} else if (performanceScore < alertThreshold) {
    alertSeverity = 'medium';
    alertMessage = '‚ö†Ô∏è MEDIUM: Performance below threshold';
} else if (performanceScore < 80) {
    alertSeverity = 'low';
    alertMessage = '‚ÑπÔ∏è LOW: Room for improvement';
}

// Key recommendations (unchanged)
const recommendations = [];
if (audits['unused-css-rules'] && audits['unused-css-rules'].score < 0.5) {
    recommendations.push('Remove unused CSS');
}
if (audits['render-blocking-resources'] && audits['render-blocking-resources'].score < 0.5) {
    recommendations.push('Eliminate render-blocking resources');
}
// ... other recommendations

return {
    json: {
        date: new Date().toISOString().split('T')[0],
        url: siteData.URL,
        site_name: siteData.Site_Name,
        category: siteData.Category || 'General',
        device: $json.device,
        performance_score: performanceScore,
        lcp: lcp,
        fid: fid,
        cls: cls,
        recommendations: recommendations.join(', ') || 'No major issues detected',
        full_report_url: response.id ? `https://pagespeed.web.dev/report?url=${encodeURIComponent(siteData.URL)}` : 'N/A',

        // IMPROVED ALERT DATA
        should_alert: shouldAlert,
        alert_threshold: alertThreshold,
        alert_severity: alertSeverity,
        alert_message: alertMessage,

        // PERFORMANCE CATEGORY
        performance_category: performanceScore >= 90 ? 'excellent' : 
                             performanceScore >= 50 ? 'needs_improvement' : 'poor'
    }
};

// Get today's date in YYYY-MM-DD format
const today = new Date().toISOString().split('T')[0];

const sitesToProcess = [];

for (const site of $input.all()) {
  const lastProcessed = site.json.Last_Processed_Date;

  // If never processed OR processed on a different date, include it
  if (!lastProcessed || lastProcessed === '' || lastProcessed !== today) {
    sitesToProcess.push(site);
  }
}

return sitesToProcess;
