// 'items' is an array of all your inputs.
// We loop through each one using .map().
const allData = $input.first().json.content.parts.map(item => {
  // Get the raw text string from the current item.
  const rawText = item.text;

  // Find and extract the clean JSON string from the code block.
  const match = rawText.match(/```json\n([\s\S]*)\n```/);
  const jsonString = match ? match[1] : '{}'; // Default to empty object if no match

  // Parse the string into a real JSON object.
  return JSON.parse(jsonString);
});

// Return a single new item containing the array of all parsed data.
return [{
  json: {
    photos_results: allData
  },
  // The index of the input item that generated this output item
  "pairedItem": 0
}];
