const webhookData = $input.first().json.body;

// Log the entire webhookData for debugging
console.log(webhookData);

//const envFilePath = $node["SetConfiguration"].json["envFilePath"];

const envFilePath = 
$input.first().json.envFilePath
// Ensure commits array exists and is not empty
const commits = webhookData.commits || [];
const changedFiles = commits.flatMap(commit => commit.added.concat(commit.modified, commit.removed));

// Check if the repository exists before accessing full_name
const repositoryName = webhookData.repository ? webhookData.repository.full_name : 'Repository not found';

// Check if the .env.staging file was changed
const envFileChanged = changedFiles.some(file => file.includes(envFilePath));

return [
  {
    json: {
      repository: repositoryName,
      ref: webhookData.ref,
      after: webhookData.after,
      envFileChanged,
      changedFiles
    }
  }
];

// Only proceed if .env.staging was changed
if (!$input.first().json.envFileChanged) {
  return [];
}

// Get configuration values
const configFiles = JSON.parse($node["SetConfiguration"].json["configFiles"]);
const targetBranch = $node["SetConfiguration"].json["targetBranch"];
const cacheInvalidationKeys = JSON.parse($node["SetConfiguration"].json["cacheInvalidationKeys"]);
const repository = $input.first().json.repository;
const commitSha = $input.first().json.after;

// Simulate diff logic for iOS
const envChanges = {
  "API_KEY": "new-api-key-value",
  "BUNDLE_VERSION": "2.0.0",
  "DEBUG_MODE": "true"
};

// Determine which config files need updates
const configUpdates = [];

// Check if cache invalidation is needed
const cacheInvalidationNeeded = cacheInvalidationKeys.some(key => envChanges.hasOwnProperty(key));

// For each config file, determine what needs to be updated
configFiles.forEach(file => {
  if (file === "Info.plist") {
    configUpdates.push({
      file,
      changes: [
        {
          key: "CFBundleShortVersionString",
          oldValue: "1.0.0",
          newValue: "2.0.0"
        },
        {
          key: "API_KEY",
          oldValue: "old-api-key-value",
          newValue: "new-api-key-value"
        }
      ]
    });
  } else if (file === "Config.xcconfig") {
    configUpdates.push({
      file,
      changes: [
        {
          key: "API_KEY",
          oldValue: "old-api-key-value",
          newValue: "new-api-key-value"
        },
        {
          key: "BUNDLE_VERSION",
          oldValue: "1.0.0",
          newValue: "2.0.0"
        }
      ]
    });
  }
});

return [
  {
    json: {
      repository,
      commitSha,
      targetBranch,
      envChanges,
      configUpdates,
      cacheInvalidationNeeded,
      cacheInvalidationKeys
    }
  }
];

// Create a unique branch name for the config sync
const data = $input.first().json;
const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
const branchName = `ios-config-sync/${timestamp}`;

return [
  {
    json: {
      ...data,
      branchName
    }
  }
];

// Check if cache invalidation is needed
const data = $input.first().json;

if (!data.cacheInvalidationNeeded) {
  return [{ json: { ...data, cacheInvalidated: false } }];
}

// In a real implementation, you would invalidate the Xcode cache here
// For iOS, this might involve cleaning derived data or triggering a cache cleanup

return [
  {
    json: {
      ...data,
      cacheInvalidated: true,
      cacheInvalidationMessage: "Xcode cache invalidation triggered for keys: " + data.cacheInvalidationKeys.join(", ")
    }
  }
];

// Prepare file updates for each iOS config file
const data = $input.first().json;

// Extract repository name from the URL
// Check if the URL exists
const url = data?.url;
let repoName = '';
let user = '';
let repoFullName = '';
let newBranch = '';
// If the URL exists, extract the repository name and user
if (url) {
  const urlParts = url.split("/repos/")[1]?.split("/");

  if (urlParts && urlParts.length > 1) {
    user = urlParts[0]; // Get the user (e.g., username)
    repoName = urlParts[1]; // Get the repository name (e.g., repo_app)
    repoFullName = `${user}/${repoName}`; // Full repository name (e.g., username/repo_app)
  }
}

const targetBranch = $node["SetConfiguration"].json["targetBranch"];

// Extract the new branch name from the `ref` field
const ref = data?.ref;
if (ref) {
  // Remove "refs/heads/" to get just the branch name
  newBranch = ref.replace("refs/heads/", "");
}

return [
  {
    json: {
      ...data,
      user,
      repoName,
      repoFullName,
      targetBranch,
      newBranch
    }
  }
];

