// Sort by Scene Number (ascending)
records.sort((a, b) => a.fields["Scene Number"] - b.fields["Scene Number"]);

// Build output object
let output = {};

// Loop through scenes dynamically
records.forEach((rec, index) => {
  const sceneNum = index + 1;

