  let fullImageUrl = null;
  if (rec.fields["Generated_Image"] && rec.fields["Generated_Image"].length > 0) {
    const attachment = rec.fields["Generated_Image"][0]; // take the first attachment
    if (attachment.thumbnails && attachment.thumbnails.full) {
      fullImageUrl = attachment.thumbnails.full.url;
