// Templates for normal tweets
const templates = [
  "transformation",
  "hook-list-takeaway",
  "interesting fact",
  "Metaphor",
  "contrast",
  "motivation",
  "triad",
  "comparison",
  "80/20 rule",
  "callout"
];

// Track last output to avoid duplicates
if (!global.lastTweet) {
  global.lastTweet = null;
}

function getRandom(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}

let tweet;
let ads = false

do {
  if (Math.random() < 0.3) {
    // 20% chance ‚Üí promo
    tweet = "advertise";
    ads = true;
  } else {
    // 80% chance ‚Üí template
    tweet = getRandom(templates);
  }
} while (tweet === global.lastTweet); // prevent repeats

// Save for next run
global.lastTweet = tweet;

return [{ json: { tweet, ads} }];

// random delay in minutes
const delayMinutes = Math.floor(Math.random() * 120); // 0‚Äì120 for 2-hour window
const delayMilliseconds = delayMinutes * 60 * 1000;

return new Promise(resolve => setTimeout(() => resolve([{ json: {} }]), delayMilliseconds ));
