// Code pour node n8n - Division de texte pour Discord
// Limite Discord : 2000 caract√®res par message

const DISCORD_CHAR_LIMIT = 2000;
const SAFETY_MARGIN = 50; // Marge de s√©curit√© pour √©viter les d√©passements
const EFFECTIVE_LIMIT = DISCORD_CHAR_LIMIT - SAFETY_MARGIN;

function splitTextForDiscord(text) {
  // Si le texte est plus court que la limite, on le retourne tel quel
  if (text.length <= EFFECTIVE_LIMIT) {
    return [text];
  }

  const messages = [];
  let currentMessage = '';

  // Diviser le texte en paragraphes d'abord (s√©par√©s par \n\n ou ----)
  const sections = text.split(/(?:\n\n|----)/);

  for (let i = 0; i < sections.length; i++) {
    let section = sections[i].trim();

    // Si une section compl√®te d√©passe la limite, la diviser par phrases
    if (section.length > EFFECTIVE_LIMIT) {
      const sentences = section.split(/(?<=[.!?:])\s+/);

      for (const sentence of sentences) {
        // Si une phrase seule d√©passe la limite, la couper brutalement
        if (sentence.length > EFFECTIVE_LIMIT) {
          const chunks = splitByLength(sentence, EFFECTIVE_LIMIT);
          for (const chunk of chunks) {
            if (currentMessage.length + chunk.length + 1 > EFFECTIVE_LIMIT) {
              if (currentMessage.trim()) {
                messages.push(currentMessage.trim());
              }
              currentMessage = chunk;
            } else {
              currentMessage += (currentMessage ? ' ' : '') + chunk;
            }
          }
        } else {
          // Ajouter la phrase si elle rentre dans le message actuel
          if (currentMessage.length + sentence.length + 1 <= EFFECTIVE_LIMIT) {
            currentMessage += (currentMessage ? ' ' : '') + sentence;
          } else {
            // Sauvegarder le message actuel et commencer un nouveau
            if (currentMessage.trim()) {
              messages.push(currentMessage.trim());
            }
            currentMessage = sentence;
          }
        }
      }
    } else {
      // La section rentre dans la limite, v√©rifier si elle rentre dans le message actuel
      if (currentMessage.length + section.length + 2 <= EFFECTIVE_LIMIT) {
        currentMessage += (currentMessage ? '\n\n' : '') + section;
      } else {
        // Sauvegarder le message actuel et commencer un nouveau
        if (currentMessage.trim()) {
          messages.push(currentMessage.trim());
        }
        currentMessage = section;
      }
    }
  }

  // Ajouter le dernier message s'il existe
  if (currentMessage.trim()) {
    messages.push(currentMessage.trim());
  }

  return messages.length > 0 ? messages : [text];
}

function splitByLength(text, maxLength) {
  const chunks = [];
  for (let i = 0; i < text.length; i += maxLength) {
    chunks.push(text.slice(i, i + maxLength));
  }
  return chunks;
}

