const inputData = $input.all();
const outputItems = [];

for (let i = 0; i < inputData.length; i++) {
  const item = inputData[i];

  // Extraire le texte √† diviser (adaptez selon votre structure de donn√©es)
  // Exemple : si votre texte est dans item.json.content
  let textToSplit = '';

  // Si c'est un tableau comme dans votre exemple
  if (item.json.data && Array.isArray(item.json.data)) {
    textToSplit = item.json.data.map(dataItem => dataItem.text || '').join('\n\n---\n\n');
  } 
  // Si c'est directement du texte
  else if (item.json.text) {
    textToSplit = item.json.text;
  }
  // Si c'est dans un autre champ
  else if (item.json.content) {
    textToSplit = item.json.content;
  }
  // Fallback : convertir tout l'objet en texte
  else {
    textToSplit = JSON.stringify(item.json, null, 2);
  }

  // Diviser le texte
  const messages = splitTextForDiscord(textToSplit);

  // Cr√©er un item de sortie pour chaque message
  messages.forEach((message, index) => {
    outputItems.push({
      json: {
        message: message,
        messageIndex: index + 1,
        totalMessages: messages.length,
        characterCount: message.length,
        originalItemIndex: i
      }
    });
  });
}

return outputItems;
