// Validate and categorize documents
const files = $input.all().map(item => item.json);
const processingQueue = {
  valid: [],
  invalid: [],
  stats: {
    totalFiles: files.length,
    pdfCount: 0,
    wordCount: 0,
    imageCount: 0,
    otherCount: 0,
    totalSizeMB: 0
  }
};

// Define supported formats
const supportedFormats = {
  pdf: ['application/pdf'],
  word: [
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    'application/msword'
  ],
  image: ['image/jpeg', 'image/png', 'image/gif']
};

files.forEach(file => {
  const mimeType = file.mimeType;
  const sizeMB = (parseInt(file.size) || 0) / (1024 * 1024);

  // Check if supported
  let fileType = 'other';
  let isValid = false;

  if (supportedFormats.pdf.includes(mimeType)) {
    fileType = 'pdf';
    isValid = true;
    processingQueue.stats.pdfCount++;
  } else if (supportedFormats.word.includes(mimeType)) {
    fileType = 'word';
    isValid = true;
    processingQueue.stats.wordCount++;
  } else if (supportedFormats.image.includes(mimeType)) {
    fileType = 'image';
    isValid = true;
    processingQueue.stats.imageCount++;
  } else {
    processingQueue.stats.otherCount++;
  }

  // Check file size (max 50MB)
  if (sizeMB > 50) {
    isValid = false;
  }

  const fileInfo = {
    ...file,
    fileType,
    sizeMB: Math.round(sizeMB * 100) / 100,
    processingPriority: sizeMB < 5 ? 'high' : sizeMB < 20 ? 'medium' : 'low',
    estimatedCredits: fileType === 'pdf' ? Math.ceil(sizeMB * 2) : 1
  };

  if (isValid) {
    processingQueue.valid.push(fileInfo);
  } else {
    processingQueue.invalid.push({
      ...fileInfo,
      reason: sizeMB > 50 ? 'File too large' : 'Unsupported format'
    });
  }

  processingQueue.stats.totalSizeMB += sizeMB;
});

// Sort by priority
processingQueue.valid.sort((a, b) => {
  const priority = { high: 1, medium: 2, low: 3 };
  return priority[a.processingPriority] - priority[b.processingPriority];
});

return [{
  json: processingQueue
}];

// Track processing results
const result = $input.first().json;
const originalFile = $node['Split Items'].json;
const startTime = new Date($node['Split Items'].context.executionTime);
const endTime = new Date();
const processingTime = (endTime - startTime) / 1000;

const processedFile = {
  // Original file info
  fileName: originalFile.name,
  fileType: originalFile.fileType,
  sizeMB: originalFile.sizeMB,

  // Processing results
  success: !result.error,
  processingTime: Math.round(processingTime * 100) / 100,
  creditsUsed: result.creditsUsed || originalFile.estimatedCredits,

  // Content info
  contentLength: result.content?.length || 0,
  wordCount: result.content?.split(' ').length || 0,

  // Error tracking
  error: result.error ? {
    message: result.error.message || 'Unknown error',
    code: result.error.code
  } : null,

  // Timestamps
  processedAt: new Date().toISOString()
};

// Quality checks
if (processedFile.success) {
  processedFile.qualityChecks = {
    hasContent: processedFile.contentLength > 100,
    reasonableLength: processedFile.wordCount > 10 && processedFile.wordCount < 100000,
    properEncoding: !result.content?.includes('ÔøΩ'),
    creditsEfficiency: processedFile.creditsUsed / processedFile.sizeMB < 5
  };

  // Overall quality score
  const checks = Object.values(processedFile.qualityChecks);
  processedFile.qualityScore = (checks.filter(c => c).length / checks.length) * 100;
}

return [{ json: processedFile }];

// Generate comprehensive analytics report
const allResults = $input.all().map(item => item.json);
const initialStats = $node['Validate & Queue Files'].json.stats;

// Calculate processing analytics
const analytics = {
  overview: {
    totalFilesFound: initialStats.totalFiles,
    filesProcessed: allResults.length,
    successfulProcessing: allResults.filter(r => r.success).length,
    failedProcessing: allResults.filter(r => !r.success).length,
    successRate: 0,
    totalProcessingTime: 0,
    totalCreditsUsed: 0,
    averageQualityScore: 0
  },

  byFileType: {
    pdf: { processed: 0, successful: 0, failed: 0, avgTime: 0, creditsUsed: 0 },
    word: { processed: 0, successful: 0, failed: 0, avgTime: 0, creditsUsed: 0 },
    image: { processed: 0, successful: 0, failed: 0, avgTime: 0, creditsUsed: 0 }
  },

  errors: {},

  performance: {
    fastestFile: null,
    slowestFile: null,
    mostEfficientCredit: null,
    leastEfficientCredit: null
  },

  quality: {
    highQuality: [],
    lowQuality: [],
    averageWordCount: 0
  }
};

// Process results
let totalQualityScore = 0;
let qualityCount = 0;

allResults.forEach(result => {
  // Update overview
  analytics.overview.totalProcessingTime += result.processingTime || 0;
  analytics.overview.totalCreditsUsed += result.creditsUsed || 0;

  // Update by file type
  const type = result.fileType;
  if (analytics.byFileType[type]) {
    analytics.byFileType[type].processed++;
    if (result.success) {
      analytics.byFileType[type].successful++;
    } else {
      analytics.byFileType[type].failed++;
    }
    analytics.byFileType[type].avgTime += result.processingTime || 0;
    analytics.byFileType[type].creditsUsed += result.creditsUsed || 0;
  }

  // Track errors
  if (result.error) {
    const errorType = result.error.message || 'Unknown';
    analytics.errors[errorType] = (analytics.errors[errorType] || 0) + 1;
  }

  // Track performance
  if (!analytics.performance.fastestFile || result.processingTime < analytics.performance.fastestFile.time) {
    analytics.performance.fastestFile = {
      name: result.fileName,
      time: result.processingTime
    };
  }
  if (!analytics.performance.slowestFile || result.processingTime > analytics.performance.slowestFile.time) {
    analytics.performance.slowestFile = {
      name: result.fileName,
      time: result.processingTime
    };
  }

  // Track quality
  if (result.qualityScore !== undefined) {
    totalQualityScore += result.qualityScore;
    qualityCount++;

    if (result.qualityScore >= 75) {
      analytics.quality.highQuality.push(result.fileName);
    } else if (result.qualityScore < 50) {
      analytics.quality.lowQuality.push(result.fileName);
    }
  }

  analytics.quality.averageWordCount += result.wordCount || 0;
});

// Calculate averages
analytics.overview.successRate = Math.round((analytics.overview.successfulProcessing / analytics.overview.filesProcessed) * 100);
analytics.overview.averageQualityScore = qualityCount > 0 ? Math.round(totalQualityScore / qualityCount) : 0;
analytics.quality.averageWordCount = Math.round(analytics.quality.averageWordCount / allResults.length);

// Calculate file type averages
Object.keys(analytics.byFileType).forEach(type => {
  const typeData = analytics.byFileType[type];
  if (typeData.processed > 0) {
    typeData.avgTime = Math.round((typeData.avgTime / typeData.processed) * 100) / 100;
    typeData.successRate = Math.round((typeData.successful / typeData.processed) * 100);
  }
});

// Generate report
let report = `# Batch Processing Analytics Report\n\n`;
report += `**Generated:** ${new Date().toLocaleString()}\n\n`;

report += `## Overview\n`;
report += `- **Files Processed:** ${analytics.overview.filesProcessed} of ${analytics.overview.totalFilesFound}\n`;
report += `- **Success Rate:** ${analytics.overview.successRate}%\n`;
report += `- **Total Processing Time:** ${Math.round(analytics.overview.totalProcessingTime)}s\n`;
report += `- **Credits Used:** ${analytics.overview.totalCreditsUsed}\n`;
report += `- **Average Quality Score:** ${analytics.overview.averageQualityScore}%\n\n`;

report += `## Performance by File Type\n`;
Object.entries(analytics.byFileType).forEach(([type, data]) => {
  if (data.processed > 0) {
    report += `### ${type.toUpperCase()}\n`;
    report += `- Processed: ${data.processed}\n`;
    report += `- Success Rate: ${data.successRate}%\n`;
    report += `- Avg Time: ${data.avgTime}s\n`;
    report += `- Credits: ${data.creditsUsed}\n\n`;
  }
});

if (Object.keys(analytics.errors).length > 0) {
  report += `## Errors Encountered\n`;
  Object.entries(analytics.errors).forEach(([error, count]) => {
    report += `- ${error}: ${count} occurrences\n`;
  });
  report += `\n`;
}

report += `## Recommendations\n`;
if (analytics.overview.successRate < 90) {
  report += `- Success rate is below 90%. Review error logs for common issues.\n`;
}
if (analytics.overview.averageQualityScore < 70) {
  report += `- Quality scores are low. Consider using 'always' LLM mode for better results.\n`;
}
if (analytics.quality.lowQuality.length > 0) {
  report += `- ${analytics.quality.lowQuality.length} files had low quality scores. Manual review recommended.\n`;
}

return [{
  json: {
    analytics,
    report,
    processedFiles: allResults,
    timestamp: new Date().toISOString()
  }
}];
