parameters.text:
=Prompt guidelines for the edited image:

Post Description: {{ $json['Post Description'] }}
Composition: {{ $json.Composition }}
Lighting: {{ $json.Lighting }}
Style: {{ $json.Style }}
Atmosphere: {{ $json.Atmosphere }}
Color Palette: {{ $json['Color Palette'] }}
Text Overlay: {{ $json['Text Overlay'] }}

parameters.jsonBody:
={
  "contents": [
    {
      "parts": [
        {
          "text": {{ JSON.stringify(
            'Original Image Prompt: ' +
            $('Analyze image').item.json.content+
            ' Requested Edit Prompt: ' +
            $json.text
          ) }}
        },
        {
          "inline_data": {
            "mime_type": "image/jpeg",
            "data": "{{ $('Get Image').item.json['Nano Image'][0].url }}"
          }
        }
      ]
    }
  ],
  "generationConfig": {
    "temperature": 0.7,
    "maxOutputTokens": 1000
  }
}
