let results = [];

for (const item of items) {
  // Get the input string from each item
  const input = item.json.output;

  // Remove markdown ```json ... ``` wrappers if present
  const cleaned = input.replace(/```json|```/g, '').trim();

  // Only try parsing if the cleaned string starts with { or [
  if (/^\s*[\{\[]/.test(cleaned)) {
    let parsed;
    try {
      parsed = JSON.parse(cleaned);
    } catch (error) {
      // Skip this item if parsing fails
      continue;
    }
    // If parsed is an array, add each object as a separate item
    if (Array.isArray(parsed)) {
      for (const obj of parsed) {
        results.push({ json: obj });
      }
    } else if (parsed && typeof parsed === 'object') {
      results.push({ json: parsed });
    }
    // If parsed is empty or not an object, skip
  }
  // If not valid JSON, skip
}

return results;
