return [{
  fullArticle: $json["fullContent"]
    .filter(p => p.trim() !== "") // remove empty <p>
    .map(p => p.replace(/\[.*?\]/g, "")) // remove [links]
    .map(p => p.replace(/\s+/g, " ").trim()) // clean spaces
    .join("\n\n") // combine into full article
}];

