parameters.message:
={{
(() => {
  const d = $node["Transcript Analysis (AI)"].json.message.content;

  const row = (label, value, i) =>
    `<tr style="background-color:${i % 2 === 0 ? '#f9f9f9' : '#ffffff'};">
       <td style="padding:12px 16px;border:1px solid #ccc;font-weight:bold;width:220px;background:#f2f2f2;">${label}</td>
       <td style="padding:12px 16px;border:1px solid #ccc;">${value || ''}</td>
     </tr>`;

  const ul = arr => `<ul style="margin:0;padding-left:22px;">${(arr || []).map(x => `<li>${x}</li>`).join('')}</ul>`;

  const actions = (d.action_items || []).length
    ? `<ol style="margin:0;padding-left:22px;">${d.action_items.map(a =>
        `<li><strong>${a.owner || '‚Äî'}</strong>: ${a.task || ''}${a.due ? ' (Due: ' + a.due + ')' : ''}</li>`
      ).join('')}</ol>`
    : "No explicit action items detected.";

  let html = `
  <div style="font-family:Arial,sans-serif;font-size:16px;line-height:1.5;">
    <h2 style="color:#2c3e50;border-bottom:2px solid #2c3e50;padding-bottom:6px;margin-bottom:12px;">Transcript Analysis</h2>
    <table style="border-collapse:collapse;border:1px solid #ccc;width:100%;font-size:16px;">
      <thead>
        <tr style="background:#2c3e50;color:#ffffff;">
          <th style="padding:12px 16px;text-align:left;width:220px;">Section</th>
          <th style="padding:12px 16px;text-align:left;">Details</th>
        </tr>
      </thead>
      <tbody>
        ${row("Executive Summary", d.summary, 0)}
        ${row("Sentiment", `<strong>Label:</strong> ${d.sentiment_label || ''}<br/><strong>Score:</strong> ${d.sentiment_score ?? ''}`, 1)}
        ${row("Key Points", ul(d.key_points), 2)}
        ${row("Action Items", actions, 3)}
        ${row("Notable Quotes", ul(d.notable_quotes), 4)}
        ${row("Topics", (d.topics || []).join(", "), 5)}
      </tbody>
    </table>
  </div>
  `;

  return html;
})()
}}
