    border-color: #2a2f3a;
    color: #d8dbe3;
  }
  input:hover, textarea:hover, select:hover { border-color: #3a4250; }
  input:focus, textarea:focus, select:focus {
    border-color: #b7a8ff;
    box-shadow: 0 0 0 4px rgba(183,168,255,.18);
  }

  .help-text { color: #9aa3b2; }

  button[type="submit"],
  .button-submit {
    box-shadow: 0 10px 26px rgba(255,109,90,.34);
  }

  .form-footer { color: #9aa3b2; }
}

