// Get markdown digest from AI output
const digest = $json["output"];
const today = new Date().toISOString().split("T")[0]; // YYYY-MM-DD

// Helper: convert a markdown line into a Notion block
function mdToBlock(line) {
  line = line.trim();

  // Headings
  if (line.startsWith("### ")) {
    return {
      object: "block",
      type: "heading_3",
      heading_3: {
        rich_text: [{ type: "text", text: { content: line.replace(/^### /, "") } }]
      }
    };
  }
  if (line.startsWith("## ")) {
    return {
      object: "block",
      type: "heading_2",
      heading_2: {
        rich_text: [{ type: "text", text: { content: line.replace(/^## /, "") } }]
      }
    };
  }
  if (line.startsWith("# ")) {
    return {
      object: "block",
      type: "heading_1",
      heading_1: {
        rich_text: [{ type: "text", text: { content: line.replace(/^# /, "") } }]
      }
    };
  }

  // Bulleted list
  if (line.startsWith("- ")) {
    const linkMatch = line.match(/\[(.*?)\]\((.*?)\)/);
    let textContent = line.replace(/^- /, "");
    let richText = [];

    if (linkMatch) {
      richText.push({
        type: "text",
        text: { content: linkMatch[1], link: { url: linkMatch[2] } }
      });
      const after = textContent.replace(linkMatch[0], "").trim();
      if (after) {
        richText.push({ type: "text", text: { content: " " + after } });
      }
    } else {
      richText.push({ type: "text", text: { content: textContent } });
    }

    return {
      object: "block",
      type: "bulleted_list_item",
      bulleted_list_item: { rich_text: richText }
    };
  }

  // Horizontal rule
  if (line.startsWith("---")) {
    return { object: "block", type: "divider", divider: {} };
  }

  // Regular paragraph
  if (line.length > 0) {
    return {
      object: "block",
      type: "paragraph",
      paragraph: { rich_text: [{ type: "text", text: { content: line } }] }
    };
  }

  // Empty line â†’ blank paragraph
  return {
    object: "block",
    type: "paragraph",
    paragraph: { rich_text: [] }
  };
}

// Convert markdown into array of Notion blocks
const blocks = digest.split("\n").map(mdToBlock).filter(Boolean);

// Split blocks into chunks of 100
const chunkSize = 100;
const chunks = [];
for (let i = 0; i < blocks.length; i += chunkSize) {
  chunks.push(blocks.slice(i, i + chunkSize));
}

