const result = [];
const row = $json;

for (const key in row) {
  const match = key.match(/(.*?) images prompt/i);
  if (match) {
    const platform = match[1].trim(); // e.g. "LinkedIn", "Twitter", "Instagram"

    // Find the actual matching key (case-insensitive, trims spaces)
    const actualKey = Object.keys(row).find(
      k => k.toLowerCase().trim() === `${platform.toLowerCase()} images prompt`
    );

    result.push({
      row_number: row.row_number,
      id: $input.first().json.Id,
      Topic: row.Topic,
      Platform: platform,
      "Image Prompt": actualKey ? row[actualKey] : "",
      "Twitter content": $input.first().json["Twitter Content Prompt"],
      "Likedin content": $input.first().json["LinkedIn Content Prompt"],
      "Instagram content": $input.first().json["Instagram Content Prompt"],
      "Image URL": row[`${platform} images url`] || "",
      "Prompt Status": row["Prompt Status"],
      "Posting Status": row["Posting Status"],
      "Posted Date": row["Posted Date"] || ""
    });
  }
}

return result;

const row = $json;
const platforms = [];

// Loop through all keys in the object
for (const key in row) {
  const match = key.match(/(.*?) images prompt/i);
  if (match) {
    const platform = match[1].trim(); 

    const entry = {
      Platform: platform,
      row_number: row.row_number,
      id: $input.first().json.Id,
      Topic: row.Topic,
      Content:"",
      Image:""
    };

    if (platform.toLowerCase() === "linkedin") {
      entry["Content"] = $input.first().json["LinkedIn Content "];
      entry["Image"]   = $input.first().json["LinkedIn images url"];
    } else if (platform.toLowerCase() === "twitter") {
      entry["Content"] = $input.first().json["Twitter Content"];
      entry["Image"]   = $input.first().json["Twitter images url"];
    }else if (platform.toLowerCase() === "instagram") {
      entry["Content"] = $input.first().json["Instagram Content"];
      entry["Image"]   = $input.first().json["Instagram images url"];
    }
    platforms.push(entry);
  }
}
return platforms;
