return items.map(item => {
  // Handle both data structures
  const raw = item.json.result?.markdown || item.json.cleanedText || item.json.html || '';

  // Add a safety check for null/undefined
  if (!raw) {
    return {
      json: {
        url: item.json.result?.url || item.json.url || '',
        cleanedText: '',
        error: 'No content found to process'
      }
    };
  }

  let cleaned = raw
    // Remove headers but keep the content structure
    .replace(/^#{1,6}\s+(.+)$/gm, '$1') // Convert headers to plain text

    // Remove markdown links but keep the text
    .replace(/\[([^\]]+)\]\([^)]+\)/g, '$1') // Keep link text, remove URL

    // Remove code blocks completely
    .replace(/```[\s\S]*?```/g, '') 
    .replace(/`([^`]+)`/g, '$1') // Remove inline code backticks but keep content

    // Remove markdown formatting
    .replace(/\*\*([^*]+)\*\*/g, '$1') // Remove bold formatting
    .replace(/\*([^*]+)\*/g, '$1') // Remove italic formatting
    .replace(/_{2,}([^_]+)_{2,}/g, '$1') // Remove underline formatting
    .replace(/~~([^~]+)~~/g, '$1') // Remove strikethrough

    // Remove lists formatting but keep content
    .replace(/^\s*[-*+]\s+/gm, '') // Remove bullet points
    .replace(/^\s*\d+\.\s+/gm, '') // Remove numbered lists

    // Remove HTML remnants
    .replace(/<[^>]*>/g, '') // Remove any remaining HTML tags
    .replace(/&[a-zA-Z0-9#]+;/g, '') // Remove HTML entities

    // Remove navigation and common web elements
    .replace(/\b(Home|About|Contact|Privacy|Terms|Login|Register|Menu|Navigation|Footer|Header|Sidebar)\b/gi, '')
    .replace(/\b(Click here|Read more|Learn more|Show more|View all|See all)\b/gi, '')
    .replace(/\b(Previous|Next|Page \d+|Back to top)\b/gi, '')

    // Remove social media and sharing text
    .replace(/\b(Share|Tweet|Facebook|LinkedIn|Instagram|Follow us|Subscribe)\b/gi, '')

    // Remove common website noise
    .replace(/\b(Cookie|Cookies|GDPR|Accept|Decline|Consent)\b/gi, '')
    .replace(/\b(Advertisement|Ad|Sponsored|Promotion)\b/gi, '')

    // Remove excessive punctuation and symbols
    .replace(/[^\w\s.,!?;:()\-"']/g, '') // Keep only essential punctuation
    .replace(/\.{2,}/g, '.') // Replace multiple dots with single dot
    .replace(/\?{2,}/g, '?') // Replace multiple question marks
    .replace(/!{2,}/g, '!') // Replace multiple exclamation marks

    // Clean up whitespace and line breaks
    .replace(/\n{3,}/g, '\n\n') // Replace multiple line breaks with double
    .replace(/\s+/g, ' ') // Normalize whitespace
    .replace(/\s*\n\s*/g, '\n') // Clean line breaks

    // Remove lines that are too short (likely noise)
    .split('\n')
    .filter(line => line.trim().length > 10) // Remove very short lines
    .join('\n')

    .trim();

  // Additional quality checks
  const wordCount = cleaned.split(/\s+/).length;
  const hasMinimumContent = wordCount >= 50; // Minimum 50 words

  // Check if content is mostly meaningful (not just numbers/symbols)
  const meaningfulContent = cleaned.replace(/[^\w\s]/g, '').length > cleaned.length * 0.7;

  // Extract additional metadata for better context
  const extractedTitle = raw.match(/^#{1,3}\s+(.+)$/m)?.[1] || '';
  const domain = (item.json.result?.url || item.json.url || '').replace(/^https?:\/\//, '').split('/')[0];

  return {
    json: {
      url: item.json.result?.url || item.json.url || '',
      cleanedText: cleaned,
      wordCount: wordCount,
      hasMinimumContent: hasMinimumContent,
      meaningfulContent: meaningfulContent,
      extractedTitle: extractedTitle,
      domain: domain,
      contentLength: cleaned.length,
      // Quality score for filtering
      qualityScore: (hasMinimumContent ? 0.5 : 0) + (meaningfulContent ? 0.5 : 0)
    }
  };
});

// Filter out low-quality content
return items.filter(item => {
  const quality = item.json.qualityScore || 0;
  const minWords = item.json.wordCount >= 50;
  const hasContent = item.json.cleanedText.length > 200;

  return quality >= 0.5 && minWords && hasContent;
});

// Content Type Detection - Fixed Version
return items.map(item => {
  const text = item.json.cleanedText || '';

  // Content type detection function
  const detectContentType = (text) => {
    if (!text || text.length < 10) {
      return 'unknown';
    }

    const lowerText = text.toLowerCase();

    // Check for code content
    if (lowerText.includes('function') || lowerText.includes('class') || 
        lowerText.includes('import') || lowerText.includes('def ') ||
        lowerText.includes('var ') || lowerText.includes('const ')) {
      return 'code';
    }

    // Check for tutorial content
    if (lowerText.includes('step 1') || lowerText.includes('tutorial') || 
        lowerText.includes('how to') || lowerText.includes('guide') ||
        lowerText.includes('walkthrough')) {
      return 'tutorial';
    }

    // Check for FAQ content
    if (lowerText.includes('faq') || lowerText.includes('q:') || 
        lowerText.includes('a:') || lowerText.includes('question') ||
        lowerText.includes('frequently asked')) {
      return 'faq';
    }

    // Check for documentation
    if (lowerText.includes('api') || lowerText.includes('documentation') ||
        lowerText.includes('reference') || lowerText.includes('manual')) {
      return 'documentation';
    }

    // Check for news/blog content
    if (lowerText.includes('published') || lowerText.includes('author') ||
        lowerText.includes('posted') || lowerText.includes('blog')) {
      return 'blog';
    }

    // Check for product/service pages
    if (lowerText.includes('price') || lowerText.includes('buy') ||
        lowerText.includes('purchase') || lowerText.includes('product')) {
      return 'product';
    }

    // Default to article
    return 'article';
  };

  // Detect content type
  const contentType = detectContentType(text);

  // Return the item with added content type
  return {
    json: {
      ...item.json, // Keep all existing data
      contentType: contentType
    }
  };
});

// Enhanced metadata extraction - Fixed Version
return items.map(item => {
  const cleaned = item.json.cleanedText || '';
  const url = item.json.url || '';
  const contentType = item.json.contentType || 'article';

  // Extract title from the cleaned text (look for first meaningful line)
  const extractTitle = (text) => {
    if (!text) return '';

    const lines = text.split('\n').filter(line => line.trim().length > 0);
    if (lines.length === 0) return '';

    // Find the first substantial line (likely the title)
    const titleLine = lines.find(line => 
      line.trim().length > 10 && 
      line.trim().length < 200 &&
      !line.includes('http') &&
      !line.includes('www.')
    );

    return titleLine ? titleLine.trim() : lines[0].trim();
  };

  // Extract domain from URL
  const extractDomain = (url) => {
    if (!url) return '';
    try {
      return url.replace(/^https?:\/\//, '').split('/')[0];
    } catch (e) {
      return '';
    }
  };

  // Count words in the text
  const countWords = (text) => {
    if (!text) return 0;
    return text.trim().split(/\s+/).filter(word => word.length > 0).length;
  };

  // Calculate quality score
  const calculateQualityScore = (text, wordCount) => {
    if (!text || wordCount < 50) return 0;

    const meaningfulContent = text.replace(/[^\w\s]/g, '').length > text.length * 0.7;
    const hasMinimumContent = wordCount >= 50;

    return (hasMinimumContent ? 0.5 : 0) + (meaningfulContent ? 0.5 : 0);
  };

  // Simple language detection (basic version)
  const detectLanguage = (text) => {
    if (!text) return 'unknown';

    // Simple heuristic - could be improved with a proper language detection library
    const commonEnglishWords = ['the', 'and', 'is', 'in', 'to', 'of', 'a', 'for', 'on', 'with'];
    const commonDutchWords = ['de', 'het', 'en', 'van', 'een', 'in', 'op', 'te', 'aan', 'met'];

    const lowerText = text.toLowerCase();
    const englishCount = commonEnglishWords.filter(word => lowerText.includes(` ${word} `)).length;
    const dutchCount = commonDutchWords.filter(word => lowerText.includes(` ${word} `)).length;

    if (englishCount > dutchCount) return 'en';
    if (dutchCount > englishCount) return 'nl';
    return 'unknown';
  };

  // Extract all metadata
  const extractedTitle = extractTitle(cleaned);
  const domain = extractDomain(url);
  const wordCount = countWords(cleaned);
  const qualityScore = calculateQualityScore(cleaned, wordCount);
  const detectedLanguage = detectLanguage(cleaned);

  // Enhanced metadata object
  const metadata = {
    page: url,
    title: extractedTitle,
    domain: domain,
    contentType: contentType,
    wordCount: wordCount,
    scrapedDate: new Date().toISOString(),
    language: detectedLanguage,
    qualityScore: qualityScore,
    contentLength: cleaned.length
  };

  return {
    json: {
      ...item.json, // Keep all existing data
      metadata: metadata,
      // Also keep individual fields for easier access
      extractedTitle: extractedTitle,
      domain: domain,
      wordCount: wordCount,
      qualityScore: qualityScore,
      detectedLanguage: detectedLanguage
    }
  };
});

// Simple counter that resets for each new task ID
if (typeof globalThis.currentTaskId === 'undefined') {
  globalThis.currentTaskId = null;
  globalThis.currentCounter = 0;
}

return items.map(item => {
  const taskId = item.json.task_id;

  // Check if this is a new task ID
  if (globalThis.currentTaskId !== taskId) {
    // New task ID detected - reset counter
    globalThis.currentTaskId = taskId;
    globalThis.currentCounter = 1;
  } else {
    // Same task ID - increment counter
    globalThis.currentCounter++;
  }

  return {
    json: {
      ...item.json,
      attempt_count: globalThis.currentCounter
    }
  };
});
