const usdcMethods = $('Assure USDC (Base)').all();
const aBasUSDCMethods = $('Assure aBasUSDC').all();
const aavePoolMethods = $('Assure Aave: Pool Proxy (Base)').all();

console.log("usdcMethods:", usdcMethods)

const usdcApprove = usdcMethods.find(m => m.json.functionName === "approve")?.json.id;
const usdcBalanceOf = usdcMethods.find(m => m.json.functionName === "balanceOf")?.json.id;
const aBasUSDCBalanceOf = aBasUSDCMethods.find(m => m.json.functionName === "balanceOf")?.json.id;
const aavePoolSupply = aavePoolMethods.find(m => m.json.functionName === "supply")?.json.id;
const aavePoolWithdraw = aavePoolMethods.find(m => m.json.functionName === "withdraw")?.json.id;

const contractMethodIds = {
