function extractCustomerName(description) {
  // Try to extract name from description
  const namePatterns = [
    /Best regards,\s*([^\n\r]+)/i,
    /Sincerely,\s*([^\n\r]+)/i,
    /Thanks,\s*([^\n\r]+)/i,
    /([A-Z][a-z]+ [A-Z][a-z]+)/
  ];

  for (const pattern of namePatterns) {
    const match = description.match(pattern);
    if (match && match[1]) {
      return match[1].trim().replace(/[^\w\s]/g, '');
    }
  }

  return 'Valued Customer';
}

function cleanSubject(subject) {
  // Remove HTML tags and clean up subject
  return subject.replace(/<[^>]*>/g, '').trim() || 'Support Request';
}

function cleanDescription(description) {
  // Clean HTML and extract first meaningful sentence
  const cleaned = description.replace(/<[^>]*>/g, '').replace(/\s+/g, ' ').trim();
  const firstSentence = cleaned.split('.')[0];
  return firstSentence.length > 100 ? firstSentence.substring(0, 100) + '...' : firstSentence + '.';
}

