// n8n Code Node - Zendesk Ticket Formatter
// This code processes Zendesk ticket data and formats it into clean JSON structure

// Function to format individual ticket data
function formatTicketData(ticketData) {
  return {
    ticket_id: ticketData.id,
    subject: ticketData.subject || "No Subject",
    description: ticketData.description || "No Description",
    status: ticketData.status,
    priority: ticketData.priority,
    type: ticketData.type || "general",
    created_at: ticketData.created_at,
    updated_at: ticketData.updated_at,
    channel: ticketData.via?.channel || "unknown",
    requester_id: ticketData.requester_id,
    assignee_id: ticketData.assignee_id,
    organization_id: ticketData.organization_id,
    group_id: ticketData.group_id,
    tags: ticketData.tags || [],
    encoded_id: ticketData.encoded_id,
    ticket_url: ticketData.url,
    is_public: ticketData.is_public,
    satisfaction_score: ticketData.satisfaction_rating?.score || "not_rated",
    custom_status_id: ticketData.custom_status_id,
    brand_id: ticketData.brand_id,
    formatted_at: new Date().toISOString(),
    is_urgent: (ticketData.tags || []).includes('urgent'),
    has_assignee: ticketData.assignee_id ? true : false
  };
}

// Function to get priority level (for sorting/filtering)
function getPriorityLevel(priority) {
  const levels = {
    'low': 1,
    'normal': 2,
    'high': 3,
    'urgent': 4
  };
  return levels[priority] || 2;
}

// Function to calculate ticket age in hours
function getTicketAgeHours(createdAt) {
  const created = new Date(createdAt);
  const now = new Date();
  return Math.round((now - created) / (1000 * 60 * 60));
}

// Loop over input items and format each Zendesk ticket
for (const item of $input.all()) {
  // Get the original ticket data
  const originalTicket = item.json;

  // Format the ticket data into clean structure
  const formattedTicket = formatTicketData(originalTicket);

  // Add additional computed fields
  item.json = {
    ...formattedTicket,
    priority_level: getPriorityLevel(originalTicket.priority),
    ticket_age_hours: getTicketAgeHours(originalTicket.created_at),
    needs_attention: (
      originalTicket.status === 'open' && 
      ((originalTicket.tags || []).includes('urgent') || 
       getTicketAgeHours(originalTicket.created_at) > 24)
    ),
    channel_type: originalTicket.via?.channel === 'web' ? 'self_service' : 
                  originalTicket.via?.channel === 'api' ? 'system_generated' : 
                  originalTicket.via?.channel || 'unknown'
  };
}

// Return all processed items
return $input.all();
