// Get workflow data from the Loop Over Workflows node
const workflow = $('Loop Over Workflows').item.json;
const config = $('Config').first().json;

// Prepare GitHub path using workflow name instead of ID
const date = new Date();
const year = date.getFullYear();
const month = String(date.getMonth() + 1).padStart(2, '0');

// Sanitize workflow name for use as filename
// Remove special characters and replace spaces with underscores
const sanitizedName = workflow.name
  .replace(/[^a-zA-Z0-9\s-_]/g, '') // Remove special characters except spaces, hyphens, underscores
  .replace(/\s+/g, '_')              // Replace spaces with underscores
  .replace(/_+/g, '_')               // Remove multiple underscores
  .trim();                           // Remove leading/trailing spaces

// Create the GitHub path with sanitized name
const githubPath = `${config.repo_path}${year}/${month}/${sanitizedName}.json`;

// Order JSON keys for consistency
const orderJsonKeys = (jsonObj) => {
  const ordered = {};
  Object.keys(jsonObj).sort().forEach(key => {
    ordered[key] = jsonObj[key];
  });
  return ordered;
};

// Prepare workflow data
const orderedWorkflow = orderJsonKeys(workflow);
const workflowJson = JSON.stringify(orderedWorkflow, null, 2);

// Return both the original data and GitHub-specific data
return {
  ...workflow,
  github_path: githubPath,
  workflow_json: workflowJson,
  year: year,
  month: month,
  sanitized_name: sanitizedName
};

