// Process all incoming items from Webhook (Run Once mode)
const items = $input.all();

return items.map(({ json }) => {
  const obj = json?.body?.data?.object ?? {};

  // Extract and split name on the FIRST space
  const fullName = String(obj.customer_name ?? "").trim().replace(/\s+/g, " ");
  const spaceIdx = fullName.indexOf(" ");
  const firstName = spaceIdx === -1 ? fullName : fullName.slice(0, spaceIdx);
  const lastName  = spaceIdx === -1 ? ""       : fullName.slice(spaceIdx + 1);

  // Other fields
  const currency           = obj.currency ?? "";
  const customer_email     = obj.customer_email ?? "";
  const hosted_invoice_url = obj.hosted_invoice_url ?? "";
  const invoice_number     = obj.number ?? "";
  const invoice_amount     = obj.total != null ? obj.total / 100 : null; // cents -> float
  const type               = json?.body?.type ?? "";
  const billing_reason     = obj.billing_reason ?? "";
  const collection         = obj.collection_method ?? "";
  const account_name       = obj.account_name ?? "";

  // Description: prefer top-level; fallback to first line item
  const lineItemDesc =
    Array.isArray(obj?.lines?.data) && obj.lines.data.length > 0
      ? obj.lines.data[0]?.description ?? ""
      : "";

  const description = obj.description ?? lineItemDesc ?? "";

  return {
    json: {
      firstName,
      lastName,
      currency,
      customer_email,
      hosted_invoice_url,
      invoice_number,
      invoice_amount,
      type,
      billing_reason,
      collection,
      description,
      account_name,
    },
  };
});

// Use this if you only ever have one incoming item.
function extractJsonString(s) {
  if (typeof s !== 'string') return '';
  let t = s.trim();
  t = t.replace(/^```(?:json)?\n?/, '').replace(/```$/, '').trim();
  if (t.startsWith('{') && t.endsWith('}')) return t;
  const m = t.match(/{[\s\S]*}/);
  return m ? m[0] : '';
}

const raw = $json?.text ?? $json?.output ?? '';
const jsonStr = extractJsonString(raw);
if (!jsonStr) {
  throw new Error('Could not find a JSON object in the model response.');
}
const obj = JSON.parse(jsonStr);
return [{ json: obj }];
