// Unified KPI Calculation Code Node for n8n Support Team Performance
// Handles both Zendesk and Freshdesk tickets
// Get all input items from n8n
const inputItems = $input.all();
let tickets = [];

// Extract tickets from all input items
inputItems.forEach(item => {
  if (item.json) {
    if (Array.isArray(item.json)) {
      // If the item contains an array of tickets
      tickets = tickets.concat(item.json);
    } else {
      // If the item is a single ticket
      tickets.push(item.json);
    }
  }
});

console.log('Total tickets processed:', tickets.length);

// Configuration thresholds (you can adjust these or get from previous node)
const thresholds = {
  response_time_hours: 4,     // 4 hours response time threshold
  resolution_time_hours: 24,  // 24 hours resolution time threshold
  csat_threshold: 4.0,        // Minimum CSAT score
  high_priority_threshold: 48, // Hours for high priority escalation
};

// Initialize KPI tracking variables
let totalTickets = tickets.length;
let resolvedTickets = 0;
let openTickets = 0;
let urgentTickets = 0;
let highPriorityTickets = 0;
let slaBreachedTickets = 0;
let channelDistribution = {};
let priorityDistribution = { low: 0, medium: 0, normal: 0, high: 0, urgent: 0 };
let platformDistribution = { zendesk: 0, freshdesk: 0, unknown: 0 };
let totalAgeHours = 0;
let needsAttentionCount = 0;
let escalatedTickets = 0;
let overdueTickets = 0;

// Enhanced status mapping for both platforms
function isTicketResolved(ticket) {
  const resolvedStatuses = {
    zendesk: ['solved', 'closed'],
    freshdesk: ['resolved', 'closed']
  };

  const platform = ticket.platform || 'zendesk';
  const normalizedStatus = ticket.status_normalized || ticket.status;

  return resolvedStatuses[platform]?.includes(normalizedStatus) || 
         resolvedStatuses[platform]?.includes(ticket.status) ||
         ['solved', 'closed', 'resolved'].includes(normalizedStatus);
}

// Enhanced priority normalization
function normalizePriority(ticket) {
  const platform = ticket.platform || 'zendesk';
  let priority = ticket.priority;

  // Handle Freshdesk numeric priorities that weren't converted
  if (platform === 'freshdesk' && typeof priority === 'number') {
    const freshdeskPriorityMap = {
      1: 'low',
      2: 'medium',
      3: 'high',
      4: 'urgent'
    };
    priority = freshdeskPriorityMap[priority] || 'normal';
  }

  // Normalize medium to normal for consistency
  if (priority === 'medium') {
    priority = 'normal';
  }

  return priority || 'normal';
}

// Check if ticket is overdue (for Freshdesk)
function isTicketOverdue(ticket) {
  if (ticket.platform === 'freshdesk' && ticket.due_by) {
    const dueDate = new Date(ticket.due_by);
    const now = new Date();
    return dueDate < now;
  }
  return false;
}

// Process each ticket
tickets.forEach(ticket => {
  // Platform distribution
  const platform = ticket.platform || 'unknown';
  platformDistribution[platform] = (platformDistribution[platform] || 0) + 1;

  // Basic counts
  if (isTicketResolved(ticket)) {
    resolvedTickets++;
  } else {
    openTickets++;
  }

  // Priority analysis with normalization
  const priority = normalizePriority(ticket);
  priorityDistribution[priority] = (priorityDistribution[priority] || 0) + 1;

  if (priority === 'urgent') {
    urgentTickets++;
  }
  if (priority === 'high' || priority === 'urgent') {
    highPriorityTickets++;
  }

  // Channel distribution
  const channel = ticket.channel || 'unknown';
  channelDistribution[channel] = (channelDistribution[channel] || 0) + 1;

  // Age and SLA analysis
  const ageHours = ticket.ticket_age_hours || 0;
  totalAgeHours += ageHours;

  // Escalation tracking (Freshdesk specific)
  if (ticket.platform === 'freshdesk' && ticket.is_escalated) {
    escalatedTickets++;
  }

  // Overdue tracking (Freshdesk specific)
  if (isTicketOverdue(ticket)) {
    overdueTickets++;
  }

  // Enhanced SLA breach conditions for both platforms
  let isSLABreached = false;

  if (isTicketResolved(ticket)) {
    // For resolved tickets, check if resolution time exceeded threshold
    if (ageHours > thresholds.resolution_time_hours) {
      isSLABreached = true;
    }
  } else {
    // For open tickets, check various conditions
    if (priority === 'urgent' && ageHours > thresholds.response_time_hours) {
      isSLABreached = true;
    } else if ((priority === 'high' || priority === 'urgent') && ageHours > thresholds.high_priority_threshold) {
      isSLABreached = true;
    } else if (ageHours > thresholds.resolution_time_hours) {
      isSLABreached = true;
    }

    // Additional Freshdesk-YOUR_OPENAI_KEY_HERE SLA conditions
    if (ticket.platform === 'freshdesk') {
      if (ticket.is_escalated || isTicketOverdue(ticket)) {
        isSLABreached = true;
      }
    }
  }

  if (isSLABreached) {
    slaBreachedTickets++;
  }

  // Needs attention count
  if (ticket.needs_attention) {
    needsAttentionCount++;
  }
});

// Calculate averages and percentages
const avgTicketAge = totalTickets > 0 ? Math.round(totalAgeHours / totalTickets) : 0;
const resolutionRate = totalTickets > 0 ? Math.round((resolvedTickets / totalTickets) * 100) : 0;
const slaBreachRate = totalTickets > 0 ? Math.round((slaBreachedTickets / totalTickets) * 100) : 0;
const urgentTicketRate = totalTickets > 0 ? Math.round((urgentTickets / totalTickets) * 100) : 0;
const highPriorityRate = totalTickets > 0 ? Math.round((highPriorityTickets / totalTickets) * 100) : 0;
const attentionRate = totalTickets > 0 ? Math.round((needsAttentionCount / totalTickets) * 100) : 0;
const escalationRate = totalTickets > 0 ? Math.round((escalatedTickets / totalTickets) * 100) : 0;
const overdueRate = totalTickets > 0 ? Math.round((overdueTickets / totalTickets) * 100) : 0;

// Platform-specific rates
const platformRates = {};
Object.keys(platformDistribution).forEach(platform => {
  if (platformDistribution[platform] > 0) {
    platformRates[platform] = Math.round((platformDistribution[platform] / totalTickets) * 100);
  }
});

// Enhanced response and resolution time estimation
const avgResponseTimeHours = Math.round(avgTicketAge * 0.15); // Estimate 15% of total age
const avgResolutionTimeHours = avgTicketAge;

// Enhanced CSAT score calculation
// In production, fetch actual satisfaction ratings from both platforms
let csatScore = 0;
let csatCount = 0;

// Simulate CSAT based on platform and performance metrics
tickets.forEach(ticket => {
  if (isTicketResolved(ticket)) {
    // Simulate CSAT based on resolution time and priority
    let simulatedCsat = 4.5;

    if (ticket.ticket_age_hours > thresholds.resolution_time_hours * 2) {
      simulatedCsat -= 1.0;
    } else if (ticket.ticket_age_hours > thresholds.resolution_time_hours) {
      simulatedCsat -= 0.5;
    }

    if (ticket.priority === 'urgent' || ticket.priority === 'high') {
      simulatedCsat -= 0.2;
    }

    // Add some randomness
    simulatedCsat += (Math.random() - 0.5) * 0.6;

    // Ensure bounds
    simulatedCsat = Math.max(1, Math.min(5, simulatedCsat));

    csatScore += simulatedCsat;
    csatCount++;
  }
});

const avgCsatScore = csatCount > 0 ? Math.round((csatScore / csatCount) * 100) / 100 : 4.0;

// Current timestamp
const now = new Date();
const dateString = now.toISOString().split('T')[0];
const timestamp = now.toISOString();

// Build comprehensive KPI object
const kpis = {
  // Metadata
  date: dateString,
  timestamp: timestamp,
  calculation_time: now.toLocaleString(),
  platforms_included: Object.keys(platformDistribution).filter(p => platformDistribution[p] > 0),

  // Volume Metrics
  total_tickets: totalTickets,
  open_tickets: openTickets,
  resolved_tickets: resolvedTickets,
  urgent_tickets: urgentTickets,
  high_priority_tickets: highPriorityTickets,
  needs_attention_count: needsAttentionCount,
  sla_breached_tickets: slaBreachedTickets,
  escalated_tickets: escalatedTickets,
  overdue_tickets: overdueTickets,

  // Performance Metrics
  avg_ticket_age_hours: avgTicketAge,
  avg_response_time_hours: avgResponseTimeHours,
  avg_resolution_time_hours: avgResolutionTimeHours,
  csat_score: avgCsatScore,
  csat_sample_size: csatCount,

  // Rate Metrics (Percentages)
  resolution_rate: resolutionRate,
  sla_breach_rate: slaBreachRate,
  urgent_ticket_rate: urgentTicketRate,
  high_priority_rate: highPriorityRate,
  attention_required_rate: attentionRate,
  escalation_rate: escalationRate,
  overdue_rate: overdueRate,

  // Distribution Analysis
  priority_distribution: priorityDistribution,
  channel_distribution: channelDistribution,
  platform_distribution: platformDistribution,
  platform_rates: platformRates,

  // Thresholds for Reference
  thresholds: thresholds
};

// Enhanced Alert Conditions
kpis.alerts = {
  high_sla_breach: slaBreachRate > 20,
  high_urgent_rate: urgentTicketRate > 15,
  high_backlog: openTickets > 10,
  low_resolution_rate: resolutionRate < 70,
  high_attention_rate: attentionRate > 50,
  long_avg_age: avgTicketAge > thresholds.resolution_time_hours,
  low_csat: avgCsatScore < thresholds.csat_threshold,
  high_escalation_rate: escalationRate > 10,
  high_overdue_rate: overdueRate > 5
};

// Overall Alert Flag
kpis.any_alert = Object.values(kpis.alerts).some(alert => alert === true);
kpis.alert_count = Object.values(kpis.alerts).filter(alert => alert === true).length;

// Enhanced Performance Grading System
let gradePoints = 0;

// Resolution rate (20 points max)
if (resolutionRate >= 90) gradePoints += 20;
else if (resolutionRate >= 80) gradePoints += 16;
else if (resolutionRate >= 70) gradePoints += 12;
else if (resolutionRate >= 60) gradePoints += 8;

// SLA compliance (20 points max)
if (slaBreachRate <= 5) gradePoints += 20;
else if (slaBreachRate <= 10) gradePoints += 16;
else if (slaBreachRate <= 15) gradePoints += 12;
else if (slaBreachRate <= 20) gradePoints += 8;

// Response time performance (20 points max)
if (avgResponseTimeHours <= 2) gradePoints += 20;
else if (avgResponseTimeHours <= 4) gradePoints += 16;
else if (avgResponseTimeHours <= 8) gradePoints += 12;
else if (avgResponseTimeHours <= 12) gradePoints += 8;

// Customer satisfaction (20 points max)
if (avgCsatScore >= 4.5) gradePoints += 20;
else if (avgCsatScore >= 4.0) gradePoints += 16;
else if (avgCsatScore >= 3.5) gradePoints += 12;
else if (avgCsatScore >= 3.0) gradePoints += 8;

// Platform-specific metrics (20 points max)
let platformBonus = 0;
if (escalationRate <= 5) platformBonus += 10;
else if (escalationRate <= 10) platformBonus += 6;

if (overdueRate <= 2) platformBonus += 10;
else if (overdueRate <= 5) platformBonus += 6;

gradePoints += platformBonus;

kpis.performance_grade = gradePoints >= 85 ? 'A' : gradePoints >= 70 ? 'B' : gradePoints >= 55 ? 'C' : 'D';
kpis.grade_percentage = gradePoints;
kpis.grade_description = 
  gradePoints >= 85 ? 'Excellent Performance' :
  gradePoints >= 70 ? 'Good Performance' :
  gradePoints >= 55 ? 'Needs Improvement' : 'Critical Issues';

// Enhanced Productivity Insights
kpis.insights = {
  busiest_channel: Object.keys(channelDistribution).length > 0 ? 
    Object.keys(channelDistribution).reduce((a, b) => 
      channelDistribution[a] > channelDistribution[b] ? a : b) : 'none',
  dominant_priority: Object.keys(priorityDistribution).length > 0 ?
    Object.keys(priorityDistribution).reduce((a, b) => 
      priorityDistribution[a] > priorityDistribution[b] ? a : b) : 'none',
  primary_platform: Object.keys(platformDistribution).length > 0 ?
    Object.keys(platformDistribution).reduce((a, b) => 
      platformDistribution[a] > platformDistribution[b] ? a : b) : 'none',
  tickets_per_priority: priorityDistribution,
  tickets_per_channel: channelDistribution,
  tickets_per_platform: platformDistribution,
  oldest_ticket_age: tickets.length > 0 ? Math.max(...tickets.map(t => t.ticket_age_hours || 0)) : 0,
  multi_platform: Object.keys(platformDistribution).filter(p => platformDistribution[p] > 0).length > 1
};

// Enhanced Recommendations based on KPIs
kpis.recommendations = [];

if (slaBreachRate > 15) {
  kpis.recommendations.push("High SLA breach rate detected - consider increasing support staff or reviewing priority workflows");
}
if (urgentTicketRate > 20) {
  kpis.recommendations.push("High percentage of urgent tickets - review escalation criteria or provide customer education");
}
if (avgTicketAge > 36) {
  kpis.recommendations.push("Long average ticket age - implement automated routing and priority queue management");
}
if (needsAttentionCount > totalTickets * 0.4) {
  kpis.recommendations.push("High attention-required rate - consider workflow automation or additional training");
}
if (resolutionRate < 75) {
  kpis.recommendations.push("Low resolution rate - focus on closing resolved tickets and improving first-contact resolution");
}
if (escalationRate > 10) {
  kpis.recommendations.push("High escalation rate - review first-line resolution capabilities and knowledge base");
}
if (overdueRate > 5) {
  kpis.recommendations.push("High overdue rate - implement better due date tracking and reminder systems");
}
if (kpis.insights.multi_platform) {
  kpis.recommendations.push("Multi-platform support detected - ensure consistent SLA and quality standards across platforms");
}

// Platform-specific insights
if (platformDistribution.freshdesk > 0) {
  kpis.freshdesk_metrics = {
    escalated_tickets: escalatedTickets,
    overdue_tickets: overdueTickets,
    escalation_rate: escalationRate,
    overdue_rate: overdueRate
  };
}

// Return the comprehensive KPI data (n8n format)
return [{ 
  json: {
    kpis: kpis,
    raw_ticket_count: totalTickets,
    processing_timestamp: timestamp
  }
}];

