// N8N Code Node - Unified Ticket Formatter (Zendesk & Freshdesk)
// This code processes both Zendesk and Freshdesk ticket data and formats them into a clean unified JSON structure

// Function to detect ticket source
function detectTicketSource(ticketData) {
  // Zendesk tickets have 'via' field and specific URL structure
  if (ticketData.via || ticketData.url?.includes('zendesk.com')) {
    return 'zendesk';
  }
  // Freshdesk tickets have 'source' as number and different structure
  if (typeof ticketData.source === 'number' || ticketData.email_config_id) {
    return 'freshdesk';
  }
  return 'unknown';
}

// Function to format Zendesk ticket data
function formatZendeskTicket(ticketData) {
  const createdAt = new Date(ticketData.created_at);
  const updatedAt = new Date(ticketData.updated_at);
  const now = new Date();

  return {
    platform: 'zendesk',
    ticket_id: ticketData.id,
    ticket_url: `https://softwarecompany-66332.zendesk.com/agent/tickets/${ticketData.id}`,
    subject: ticketData.subject || "No Subject",
    priority: ticketData.priority || "normal",
    priority_level: getZendeskPriorityLevel(ticketData.priority),
    status: ticketData.status,
    status_normalized: normalizeZendeskStatus(ticketData.status),
    created_timestamp: createdAt.toISOString(),
    updated_timestamp: updatedAt.toISOString(),
    original_created_at: ticketData.created_at,
    description_preview: cleanDescription(ticketData.description),
    description: ticketData.description || "No Description",
    tags: Array.isArray(ticketData.tags) ? ticketData.tags.join(', ') : '',
    requester_id: ticketData.requester_id,
    assignee_id: ticketData.assignee_id || 'Unassigned',
    channel: ticketData.via?.channel || "unknown",
    ticket_age_hours: Math.round((now - createdAt) / (1000 * 60 * 60)),
    needs_attention: checkZendeskNeedsAttention(ticketData, now, createdAt),
    ticket_type: ticketData.type || 'ticket'
  };
}

// Function to format Freshdesk ticket data
function formatFreshdeskTicket(ticketData) {
  const createdAt = new Date(ticketData.created_at);
  const updatedAt = new Date(ticketData.updated_at);
  const now = new Date();

  return {
    platform: 'freshdesk',
    ticket_id: ticketData.id,
    ticket_url: `https://your-domain.freshdesk.com/helpdesk/tickets/${ticketData.id}`, // Update with your domain
    subject: ticketData.subject || "No Subject",
    priority: getFreshdeskPriorityText(ticketData.priority),
    priority_level: ticketData.priority || 1,
    status: getFreshdeskStatusText(ticketData.status),
    status_normalized: normalizeFreshdeskStatus(ticketData.status),
    created_timestamp: createdAt.toISOString(),
    updated_timestamp: updatedAt.toISOString(),
    original_created_at: ticketData.created_at,
    description_preview: cleanDescription(ticketData.structured_description),
    description: ticketData.structured_description || "No Description",
    tags: Array.isArray(ticketData.tags) ? ticketData.tags.join(', ') : '',
    requester_id: ticketData.requester_id,
    assignee_id: ticketData.responder_id || 'Unassigned',
    channel: getFreshdeskSourceText(ticketData.source),
    ticket_age_hours: Math.round((now - createdAt) / (1000 * 60 * 60)),
    needs_attention: checkFreshdeskNeedsAttention(ticketData, now, createdAt),
    ticket_type: ticketData.type || 'ticket',
    due_by: ticketData.due_by,
    is_escalated: ticketData.is_escalated || false,
    sentiment_score: ticketData.sentiment_score
  };
}

// Helper functions for Zendesk
function getZendeskPriorityLevel(priority) {
  const levels = {
    'low': 1,
    'normal': 2,
    'high': 3,
    'urgent': 4
  };
  return levels[priority] || 2;
}

function normalizeZendeskStatus(status) {
  const statusMap = {
    'new': 'open',
    'open': 'open',
    'pending': 'pending',
    'solved': 'closed',
    'closed': 'closed'
  };
  return statusMap[status] || status;
}

function checkZendeskNeedsAttention(ticketData, now, createdAt) {
  const ageHours = Math.round((now - createdAt) / (1000 * 60 * 60));
  const hasUrgentTag = (ticketData.tags || []).includes('urgent');
  const isHighPriority = ['high', 'urgent'].includes(ticketData.priority);

  return ageHours > 24 || hasUrgentTag || isHighPriority;
}

// Helper functions for Freshdesk
function getFreshdeskPriorityText(priority) {
  const priorityMap = {
    1: 'low',
    2: 'medium',
    3: 'high',
    4: 'urgent'
  };
  return priorityMap[priority] || 'medium';
}

function getFreshdeskStatusText(status) {
  const statusMap = {
    2: 'open',
    3: 'pending',
    4: 'resolved',
    5: 'closed'
  };
  return statusMap[status] || 'open';
}

function normalizeFreshdeskStatus(status) {
  const statusMap = {
    2: 'open',
    3: 'pending',
    4: 'closed',
    5: 'closed'
  };
  return statusMap[status] || 'open';
}

function getFreshdeskSourceText(source) {
  const sourceMap = {
    1: 'email',
    2: 'portal',
    3: 'phone',
    7: 'chat',
    8: 'mobihelp',
    9: 'feedback_widget',
    10: 'outbound_email'
  };
  return sourceMap[source] || 'unknown';
}

function checkFreshdeskNeedsAttention(ticketData, now, createdAt) {
  const ageHours = Math.round((now - createdAt) / (1000 * 60 * 60));
  const isHighPriority = ticketData.priority >= 3; // High or Urgent
  const isEscalated = ticketData.is_escalated;
  const isOverdue = ticketData.due_by ? new Date(ticketData.due_by) < now : false;

  return ageHours > 24 || isHighPriority || isEscalated || isOverdue;
}

// Common helper function to clean description for preview
function cleanDescription(description) {
  if (!description) return "No description available";

  const cleaned = description
    .replace(/<[^>]*>/g, '') // Remove HTML tags
    .replace(/\s+/g, ' ') // Replace multiple spaces with single space
    .trim();

  return cleaned.length > 100 ? cleaned.substring(0, 100) + '...' : cleaned;
}

// Main processing function
function formatTicketData(ticketData) {
  const source = detectTicketSource(ticketData);

  switch (source) {
    case 'zendesk':
      return formatZendeskTicket(ticketData);
    case 'freshdesk':
      return formatFreshdeskTicket(ticketData);
    default:
      console.warn('Unknown ticket source:', ticketData);
      return {
        platform: 'unknown',
        ticket_id: ticketData.id,
        subject: ticketData.subject || "No Subject",
        error: 'Unable to determine ticket platform',
        raw_data: ticketData
      };
  }
}

// Process all input items
const processedItems = [];
for (const item of $input.all()) {
  const originalTicket = item.json;
  const formattedTicket = formatTicketData(originalTicket);

  processedItems.push({
    json: formattedTicket
  });
}

return processedItems;
