// Extract error details and format for logging
const errorData = $input.item.json;

const formattedError = {
  timestamp: new Date().toISOString(),
  workflowName: $workflow.name,
  executionId: $execution.id,
  nodeName: errorData.node?.name || 'Unknown Node',
  errorMessage: errorData.error?.message || 'Unknown Error',
  errorType: errorData.error?.name || 'Error',
  ticketId: errorData.error?.context?.itemIndex !== undefined 
    ? `Item ${errorData.error.context.itemIndex}` 
    : 'N/A',
  stackTrace: errorData.error?.stack?.substring(0, 500) || 'No stack trace',
  workflowStatus: 'FAILED',
  severity: 'HIGH'
};

return { json: formattedError };

// Log successful execution summary
const items = $input.all();

const summary = {
  timestamp: new Date().toISOString(),
  workflowName: $workflow.name,
  executionId: $execution.id,
  status: 'SUCCESS',
  ticketsProcessed: items.length,
  message: `Successfully processed ${items.length} tickets`
};

return { json: summary };
