// Extract the array of comment items from the API response
const comments = $input.first().json.body.items || [];

// Extract authorDisplayName safely
const authors = comments
  .map(item => {
    // Check if snippet and topLevelComment exist before accessing authorDisplayName
    if (item.snippet && item.snippet.topLevelComment && item.snippet.topLevelComment.snippet) {
      return item.snippet.topLevelComment.snippet.authorDisplayName;
    }
    return null; // or undefined
  })
  .filter(name => !!name); // filter out null/undefined

if (authors.length === 0) {
  // No authors found, return empty or null
  return [{ json: { randomAuthorDisplayName: null } }];
}

// Randomly select one authorDisplayName
const randomIndex = Math.floor(Math.random() * authors.length);
const randomAuthor = authors[randomIndex];

// Return as output
return [{ json: { randomAuthorDisplayName: randomAuthor } }];
