function normalizeUrl(url) {
  if (!/^https?:\/\//i.test(url)) {
    return "https://" + url;
  }
  return url;
}

const inputUrl = $json["set_raw_data"];
const outputUrl = normalizeUrl(inputUrl);

return [
  {
    json: {
      normalizedUrl: outputUrl
    }
  }
];
