dataFields.fieldValue:
={{ 
  // Take the "name" field from the current JSON item
  $json.name
    // Remove leading/trailing spaces (just in case)
    .trim()
    // Split the string into an array, using a space as the separator
    .split(" ")
    // Select the first element of the array (the first word)
    [0] 
}}

={{ 
  /* take "name" or empty string if missing */ ($json.name || '')
    /* trim leading/trailing whitespace */ .trim()
    /* split into words by any whitespace */ .split(/\s+/)
    /* get last word */ .slice(-1)[0]
    /* fallback */ || ''
}}

