const items = $input.all();

return items.map(item => {
  const plan = item.json.Plan || 'free';

  let tier, priority;
  switch(plan.toLowerCase()) {
    case 'enterprise':
      tier = 'Premium Onboarding';
      priority = 'High';
      break;
    case 'pro':
      tier = 'Standard Onboarding';
      priority = 'Medium';
      break;
    default:
      tier = 'Basic Onboarding';
      priority = 'Low';
  }

  return {
    json: {
      // Keep the original data structure (capitalized from Google Sheets)
      Name: item.json.Name,
      Email: item.json.Email,
      Company: item.json.Company,
      Plan: item.json.Plan,
      Phone: item.json.Phone,
      Status: item.json.Status,
      Timestamp: item.json.Timestamp,
      // Add new fields only
      tier,
      priority,
      onboarding_id: `ONB-${Date.now()}`,
      welcome_template: tier === 'Premium Onboarding' ? 'enterprise_welcome' : 
                        tier === 'Standard Onboarding' ? 'pro_welcome' : 'basic_welcome'
    }
  };
});

// Get all input items
const items = $input.all();

// Calculate date one week ago in IST
const oneWeekAgo = new Date();
oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
oneWeekAgo.setHours(0, 0, 0, 0); // Set to start of day for accurate comparison

// Filter recent clients based on Onboarding Date
const recentClients = items
  .map(item => item.json) // Extract the json object from each item
  .filter(row => {
    const rowDate = new Date(row["Onboarding Date"]); // Use Onboarding Date field
    return rowDate >= oneWeekAgo && !isNaN(rowDate); // Ensure valid date
  });

// Process statistics
const totalClients = recentClients.length;
const planBreakdown = recentClients.reduce((acc, client) => {
  const plan = client.Plan; // Use Plan property
  acc[plan] = (acc[plan] || 0) + 1;
  return acc;
}, {});

const tierBreakdown = recentClients.reduce((acc, client) => {
  const tier = client.Tier; // Use Tier property
  acc[tier] = (acc[tier] || 0) + 1;
  return acc;
}, {});

const earliestOnboarding = recentClients.length > 0
  ? recentClients.reduce((earliest, client) => {
      const clientDate = new Date(client["Onboarding Date"]);
      return !earliest || clientDate < earliest ? clientDate : earliest;
    }, null)
  : null;

const report = {
  week_ending: new Date().toLocaleDateString('en-IN', { timeZone: 'Asia/Kolkata' }), // e.g., "25/09/2025"
  total_new_clients: totalClients,
  plan_breakdown: planBreakdown,
  tier_breakdown: tierBreakdown,
  earliest_onboarding: earliestOnboarding ? earliestOnboarding.toISOString().split('T')[0] : "N/A",
  client_list: recentClients.map(client => ({
    name: client.Name,
    email: client.Email,
    company: client.Company,
    plan: client.Plan,
    onboarding_date: client["Onboarding Date"],
    tier: client.Tier,
    onboarding_id: client["Onboarding ID"],
    priority: client.Priority
  }))
};

// Return the report as a single JSON object
return [{ json: report }];

