// Get data from Remove Duplicates node (clean single item)
const data = $input.all()[0].json;

// Note: Some fields have a space at the beginning, so we need to handle that
const cleanData = {
  instructions: data.instructions,
  qr_url: data.qr_url,
  return_deadline: data.return_deadline,
  tracking_url: data.tracking_url,
  customer_name: data.customer_name,
  customer_email: data.customer_email,
  order_id: data[' order_id'] || data.order_id, // Handle space in field name
  return_reason: data.return_reason,
  product_name: data[' product_name'] || data.product_name, // Handle space in field name
  purchase_date: data[' purchase_date'] || data.purchase_date // Handle space in field name
};

