// Return the result
return [{
    json: {
        html_content: html,
        customer_name: cleanData.customer_name,
        customer_email: cleanData.customer_email,
        order_id: cleanData.order_id,
        file_name: `return_guide_${cleanData.order_id}`
    }
}];
