// Transform raw Stripe dispute data into formatted output for notifications
const items = $input.all();

return items.map(item => {
  const dispute = item.json.data[0]; // Get first dispute from array

  // Format amount with currency
  const amount = dispute.amount / 100; // Convert from cents
  const currency = dispute.currency.toUpperCase();
  const formatted_amount = `${currency} ${amount.toFixed(2)}`;

  // Calculate days until evidence deadline
  const evidenceDate = new Date(dispute.evidence_details.due_by * 1000);
  const now = new Date();
  const days_until_deadline = Math.ceil((evidenceDate - now) / (1000 * 60 * 60 * 24));

  // Determine priority based on days remaining and amount
  let priority = 'Low';
  if (days_until_deadline <= 3 || amount >= 500) {
    priority = 'High';
  } else if (days_until_deadline <= 7 || amount >= 100) {
    priority = 'Medium';
  }

  // Get customer information
  const customer_info = dispute.charge?.billing_details?.email || 
                       dispute.charge?.customer || 
                       'Unknown customer';

  return {
    json: {
      ...dispute,
      formatted_amount,
      priority,
      days_until_deadline,
      evidence_deadline: evidenceDate.toLocaleDateString(),
      created_date: new Date(dispute.created * 1000).toLocaleDateString(),
      customer_info,
      dispute_id: dispute.id,
      charge_id: dispute.charge?.id || 'N/A',
      payment_intent_id: dispute.payment_intent || 'N/A'
    }
  };
});
