// Deduplicate articles based on URL and prepare for Gemini scoring
const items = $input.all();
const uniqueArticles = new Map();
const locations = ['Phoenix', 'Scottsdale', 'Paradise Valley', 'Arizona', 'Tempe', 'Mesa', 'Chandler'];

// Process and deduplicate
for (const item of items) {
  const url = item.json.link || item.json.url || '';
  const title = item.json.title || '';
  const description = item.json.description || item.json.content || item.json.summary || '';
  const source = item.json.source || 'Unknown';
  const pubDate = item.json.pubDate || item.json.published || new Date().toISOString();

  // Create unique key based on URL or title
  const key = url || title;

  if (key && !uniqueArticles.has(key)) {
    // Check if article mentions any of our locations
    const fullText = `${title} ${description}`.toLowerCase();
    const mentionsLocation = locations.some(loc => fullText.includes(loc.toLowerCase()));

    uniqueArticles.set(key, {
      title: title.substring(0, 200),
      url: url,
      description: description.substring(0, 500),
      source: source,
      pubDate: pubDate,
      mentionsLocation: mentionsLocation
    });
  }
}

// Convert to array and return
const deduplicatedArticles = Array.from(uniqueArticles.values());

// Sort by date (newest first) and filter to most recent 50 for processing
const sortedArticles = deduplicatedArticles
  .sort((a, b) => new Date(b.pubDate) - new Date(a.pubDate))
  .slice(0, 50);

return sortedArticles.map(article => ({ json: article }));

// Prepare batch of articles for Gemini scoring - ONLY TITLES for efficiency
const articles = $input.all().map(item => item.json);

// Create a text summary for Gemini to score - ONLY TITLES to save tokens
const titlesText = articles.map((article, index) => {
  return `${index + 1}. ${article.title}`;
}).join('\n');

// Return both the articles and the formatted text
return [{
  json: {
    articles: articles,
    titlesText: titlesText,
    articlesCount: articles.length
  }
}];

