// Process Gemini response and add scores to articles
const geminiResponse = $input.first().json;
const preparedData = $('Prepare for Gemini').first().json;
const articles = preparedData.articles;

// Extract scores from Gemini response
let scores = [];
try {
  // Parse the response from Gemini
  const responseText = geminiResponse.candidates[0].content.parts[0].text;

  // Try to extract JSON array from the response
  const jsonMatch = responseText.match(/\[.*\]/s);
  if (jsonMatch) {
    scores = JSON.parse(jsonMatch[0]);
  } else {
    // Fallback: assign default scores based on location mentions
    scores = articles.map(article => article.mentionsLocation ? 75 : 50);
  }
} catch (error) {
  console.error('Error parsing Gemini response:', error);
  // Fallback scoring based on location mentions
  scores = articles.map(article => article.mentionsLocation ? 75 : 50);
}

// Combine articles with scores
const scoredArticles = articles.map((article, index) => ({
  ...article,
  relevanceScore: scores[index] || 50
}));

