const result = $input.all().map(item => {
  const body = item.json;

  return {
    customer_id: body.id,
    fulfillment_id: body.id,
    order_id: body.order_id,
    status: body.status,
    created_at: body.created_at,
    updated_at: body.updated_at,
    tracking_company: body.tracking_company,
    tracking_number: body.tracking_number,
    tracking_url: body.tracking_url,
    product_title: body.line_items?.[0]?.title || '',
    customer_name: body.destination?.name || '',
    country: body.destination?.country || '',
    shop_domain: item.json.headers?.['x-shopify-shop-domain'] || '',
  };
});

return result;

const items = $input.all();

const updatedItems = items.map((item) => {
  const customer = item?.json?.order?.customer || {};

  const rawPhone = customer?.phone || "";
  const phoneStr = typeof rawPhone === 'string' ? rawPhone : String(rawPhone || "");
  const cleanedPhone = phoneStr.replace(/\D/g, "");

  // Combine first and last name into full name
  const firstName = customer.first_name || "";
  const lastName = customer.last_name || "";
  const fullName = `${firstName} ${lastName}`.trim();

  return {
    json: {
      customer_id: customer.id || "",
      name: fullName,
      email: customer.email || "",
      phone: cleanedPhone
    }
  };
});

return updatedItems;
