// Grab the raw data from all inputs
const inputData = $input.all().map(item => item.json);

// Separate out the Google Drive items (no "metadata") vs. Supabase items (have "metadata")
const googleDriveItems = inputData.filter(item => item.id && !item.metadata);
const supabaseItems = inputData.filter(item => item.metadata);

// Extract just the file IDs from Google Drive
const googleDriveIds = googleDriveItems.map(item => item.id);

// Build a Set for quick membership checks (recommended for large arrays)
const driveIdSet = new Set(googleDriveIds);

// Filter Supabase rows to find those whose file_id is not in the Drive set
const orphanedSupabaseRows = supabaseItems.filter(item => {
  return !driveIdSet.has(item.metadata.file_id);
});

// Return each orphaned row as a separate n8n item
return orphanedSupabaseRows.map(row => ({ json: row }));

// Simple code to handle empty Google Drive

// Determine if we have Google Drive data
let hasDriveData = false;
let driveFileIds = new Set();

// Check the first item to see if it's a Google Drive file or a file list
if (items[0] && items[0].json) {
  // Is it a files array?
  if (items[0].json.files && Array.isArray(items[0].json.files)) {
    hasDriveData = true;
    for (const file of items[0].json.files) {
      if (file && file.id) driveFileIds.add(file.id);
    }
  }
  // Is it a single file with no metadata-like properties?
  else if (items[0].json.id && !items[0].json.title && !items[0].json.created_at) {
    hasDriveData = true;
    driveFileIds.add(items[0].json.id);
  }
}

console.log("Has Drive data:", hasDriveData);
console.log("Drive file IDs:", Array.from(driveFileIds));

// Determine which items are metadata records
let metadataRecords = [];

// If the first item looks like a metadata record (has title, url, created_at)
if (!hasDriveData) {
  // All items are metadata records
  metadataRecords = items.map(item => item.json).filter(Boolean);
  console.log("All items are metadata records:", metadataRecords.length);
} else {
  // Only items after the first are metadata records
  metadataRecords = items.slice(1).map(item => item.json).filter(Boolean);
  console.log("Items after first are metadata records:", metadataRecords.length);
}

// If there are no Drive files, mark all metadata records for deletion
const recordsToDelete = [];

if (driveFileIds.size === 0) {
  console.log("No Drive files, marking ALL metadata records for deletion");
  for (const record of metadataRecords) {
    if (record && record.id) {
      recordsToDelete.push({
        ...record,
        _shouldDelete: true
      });
    }
  }
} else {
  // Otherwise, only mark records not in Drive
  console.log("Checking metadata records against Drive files");
  for (const record of metadataRecords) {
    if (record && record.id && !driveFileIds.has(record.id)) {
      recordsToDelete.push({
        ...record,
        _shouldDelete: true
      });
    }
  }
}

console.log("Records to delete:", recordsToDelete.length);
return recordsToDelete;
