function toNum(x, def = 0) {
  if (x === undefined || x === null || x === '') return def;
  const n = Number(x);
  return isNaN(n) ? def : n;
}

// --- group by hour ---
const grouped = {};
for (const item of items) {
  const j = item.json;
  const hour = j.start;

  if (!grouped[hour]) {
    grouped[hour] = { start: hour, AP: 0, AM: 0, m180: null, m280: null };
  }

  grouped[hour].AP += toNum(j.AP);
  grouped[hour].AM += toNum(j.AM);

  if (j['1_8_0'] !== undefined) grouped[hour].m180 = toNum(j['1_8_0']);
  if (j['2_8_0'] !== undefined) grouped[hour].m280 = toNum(j['2_8_0']);
}

// --- sort by time ---
const hours = Object.values(grouped).sort(
  (a, b) => Date.parse(a.start) - Date.parse(b.start)
);

// --- forward pass ---
let last180 = null;
let last280 = null;

for (const h of hours) {
  if (h.m180 !== null) last180 = h.m180;
  if (h.m280 !== null) last280 = h.m280;

  if (last180 === null) last180 = null;  // defer until back-calc
  if (last280 === null) last280 = null;

  h.start180 = last180;
  h.start280 = last280;

  if (last180 !== null) last180 += h.AP;
  if (last280 !== null) last280 += h.AM;

  h.end180 = last180;
  h.end280 = last280;
}


// --- back calculation till first available 180 280 data ---

const firstMeterIndex = hours.findIndex(h => h.start180 !== null || h.start280 !== null);

if (firstMeterIndex > 0) {

  // Take the first available meter values (if missing, rebuild them)
  let base180 = hours[firstMeterIndex].start180;
  let base280 = hours[firstMeterIndex].start280;

  // If one meter exists but the other doesn't, initialize cleanly.
  if (base180 === null) base180 = 0;
  if (base280 === null) base280 = 0;

  // Backwards reconstruction
  for (let i = firstMeterIndex - 1; i >= 0; i--) {
    const h = hours[i];

    base180 -= h.AP;
    base280 -= h.AM;

    h.start180 = base180;
    h.start280 = base280;
    h.end180 = base180 + h.AP;
    h.end280 = base280 + h.AM;
  }
}


// --- build output ---

return hours.map(h => ({
  json: {
    start: h.start,
    AP: h.AP.toFixed(3),
    AM: h.AM.toFixed(3),
    '1_8_0': h.start180.toFixed(3),
    '2_8_0': h.start280.toFixed(3),
  }
}));
